import React, { useEffect, useState } from "react";
import api from "../api";

const StockTransactionPage = () => {
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]);
  const [selectedAssetTag, setSelectedAssetTag] = useState("");
  const [transactionType, setTransactionType] = useState("IN");
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [selectedEmployeeId, setSelectedEmployeeId] = useState("");
  const [employeeSearch, setEmployeeSearch] = useState("");
  const [remark, setRemark] = useState("");

  // 取得資產列表
  const fetchAssets = async () => {
    try {
      const res = await api.get("inventory/assets/");
      setAssets(res.data);
    } catch (err) {
      console.error(err);
      alert("載入資產失敗");
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  // 取得部門列表
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await api.get("departments/");
        setDepartments(res.data);
      } catch (err) {
        console.error(err);
        alert("載入部門失敗");
      }
    };
    fetchDepartments();
  }, []);

  // 根據部門取得員工
  useEffect(() => {
    const fetchEmployees = async () => {
      if (!selectedDepartment) {
        setEmployees([]);
        setFilteredEmployees([]);
        return;
      }
      try {
        const res = await api.get(`people/?department=${selectedDepartment}`);
        setEmployees(res.data);
        setFilteredEmployees(res.data);
      } catch (err) {
        console.error(err);
        alert("載入員工失敗");
      }
    };
    fetchEmployees();
  }, [selectedDepartment]);

  // 即時搜尋員工
  useEffect(() => {
    const filtered = employees.filter(
      (e) =>
        e.name.toLowerCase().includes(employeeSearch.toLowerCase()) ||
        e.id_number.toLowerCase().includes(employeeSearch.toLowerCase())
    );
    setFilteredEmployees(filtered);
  }, [employeeSearch, employees]);

  // 根據交易類型篩選可選資產
  const availableAssets = assets.filter((a) =>
    transactionType === "OUT" ? a.holder === null : a.holder !== null
  );

  const handleSubmit = async () => {
    if (!selectedAssetTag) {
      alert("請選擇資產");
      return;
    }
    if (transactionType === "OUT" && !selectedEmployeeId) {
      alert("出庫需選擇員工");
      return;
    }

    try {
      const payload = {
        asset_tag: selectedAssetTag,
        transaction_type: transactionType,
        employee_id: transactionType === "OUT" ? selectedEmployeeId : null,
        remark,
      };
      await api.post("inventory/stock_transaction/", payload);
      alert("操作成功");

      // 重置表單
      setSelectedAssetTag("");
      setTransactionType("IN");
      setSelectedDepartment("");
      setSelectedEmployeeId("");
      setEmployeeSearch("");
      setRemark("");

      // 自動刷新資產列表
      fetchAssets();
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>商品出入庫</h1>

      <div style={{ marginBottom: "10px" }}>
        <label>交易類型：</label>
        <select
          value={transactionType}
          onChange={(e) => setTransactionType(e.target.value)}
        >
          <option value="IN">入庫</option>
          <option value="OUT">出庫</option>
        </select>
      </div>

      <div style={{ marginBottom: "10px" }}>
        <label>選擇資產：</label>
        <select
          value={selectedAssetTag}
          onChange={(e) => setSelectedAssetTag(e.target.value)}
        >
          <option value="">請選擇資產</option>
          {availableAssets.map((a) => (
            <option key={a.asset_tag} value={a.asset_tag}>
              {a.product_name} ({a.product_code}) {a.holder ? `持有人: ${a.holder.name}` : "庫存"}
            </option>
          ))}
        </select>
      </div>

      {transactionType === "OUT" && (
        <>
          <div style={{ marginBottom: "10px" }}>
            <label>選擇部門：</label>
            <select
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
            >
              <option value="">-- 請選擇部門 --</option>
              {departments.map((d) => (
                <option key={d.id} value={d.name}>
                  {d.name}
                </option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>搜尋員工：</label>
            <input
              type="text"
              placeholder="姓名或編號"
              value={employeeSearch}
              onChange={(e) => setEmployeeSearch(e.target.value)}
            />
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>選擇員工：</label>
            <select
              value={selectedEmployeeId}
              onChange={(e) => setSelectedEmployeeId(e.target.value)}
            >
              <option value="">-- 請選擇員工 --</option>
              {filteredEmployees.map((emp) => (
                <option key={emp.id_number} value={emp.id_number}>
                  {emp.name} ({emp.id_number})
                </option>
              ))}
            </select>
          </div>
        </>
      )}

      <div style={{ marginBottom: "10px" }}>
        <label>備註：</label>
        <input
          type="text"
          value={remark}
          onChange={(e) => setRemark(e.target.value)}
        />
      </div>

      <button onClick={handleSubmit}>提交</button>

      <h2 style={{ marginTop: "20px" }}>資產列表</h2>
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>#</th>
            <th>名稱</th>
            <th>財產編號</th>
            <th>種類</th>
            <th>價格</th>
            <th>持有人</th>
          </tr>
        </thead>
        <tbody>
          {assets.map((a, idx) => (
            <tr key={a.asset_tag}>
              <td>{idx + 1}</td>
              <td>{a.product_name}</td>
              <td>{a.product_code}</td>
              <td>{a.type}</td>
              <td>{a.price}</td>
              <td>{a.holder ? a.holder.name : "無"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTransactionPage;
