import React, { useEffect, useState } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [editingPerson, setEditingPerson] = useState(null);
  const [selectedIds, setSelectedIds] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });
  const [searchTerm, setSearchTerm] = useState("");
  const [departmentFilter, setDepartmentFilter] = useState("");
  const [departments, setDepartments] = useState([]);
  const [previewData, setPreviewData] = useState([]);

  // Ë°®ÂñÆÊ¨Ñ‰Ωç
  const fields = [
    { name: "name", label: "ÂßìÂêç", type: "text" },
    { name: "id_number", label: "Ë∫´‰ªΩË≠âÂ≠óËôü", type: "text" },
    { name: "email", label: "‰ø°ÁÆ±", type: "email" },
    { name: "phone", label: "ÈõªË©±", type: "text" },
    { name: "department", label: "ÈÉ®ÈñÄ", type: "text" },
    { name: "title", label: "ËÅ∑Á®±", type: "text" },
    { name: "eip_account", label: "EIP Â∏≥Ëôü", type: "text" },
  ];

  // üöÄ ÂàùÂßãÂåñÔºöÂèñÂæóÊâÄÊúâ‰∫∫Âì° + ÈÉ®ÈñÄÊ∏ÖÂñÆ
  useEffect(() => {
    fetchPeople();
    fetchDepartments();
  }, []);

  const fetchPeople = async (department = "") => {
    try {
      const res = await api.get("people/", {
        params: department ? { department } : {},
      });
      setPeople(res.data);
    } catch (err) {
      console.error("ËÆÄÂèñ‰∫∫Âì°Â§±Êïó:", err);
    }
  };

  const fetchDepartments = async () => {
    try {
      const res = await api.get("departments/");
      setDepartments(res.data.map((d) => d.name));
    } catch (err) {
      console.warn("ËÆÄÂèñÈÉ®ÈñÄÊ∏ÖÂñÆÂ§±Êïó (ÈùûÂøÖË¶Å):", err);
    }
  };

  // ÂñÆÁ≠ÜÊñ∞Â¢û / Á∑®ËºØ
  const handleAddSingle = async (person) => {
    try {
      const payload = {
        name: person.name?.trim() ?? "",
        id_number: person.id_number?.trim() ?? "",
        email: person.email?.trim() ?? "",
        phone: person.phone?.trim() ?? "",
        department: person.department?.trim() ?? "",
        title: person.title?.trim() ?? "",
        eip_account: person.eip_account?.trim() ?? "",
      };

      if (editingPerson) {
        const res = await api.put(`people/${editingPerson.id_number}/`, payload);
        setPeople((prev) =>
          prev.map((p) => (p.id_number === editingPerson.id_number ? res.data : p))
        );
        setEditingPerson(null);
      } else {
        const res = await api.post("people/", payload);
        setPeople((prev) => [...prev, res.data]);
      }
    } catch (err) {
      console.error("Êñ∞Â¢û/Êõ¥Êñ∞Â§±Êïó:", err.response?.data || err);
      alert("Êìç‰ΩúÂ§±ÊïóÔºåË´ãÊ™¢Êü• console„ÄÇ");
    }
  };

  // üì¶ CSV ÂåØÂÖ•È†êË¶Ω + ÊâπÊ¨°‰∏äÂÇ≥
  const handleAddCSV = (csvData) => {
    setPreviewData(csvData);
    if (csvData.length === 0) {
      alert("CSV Ê™îÊ°àÁÇ∫Á©∫ÔºÅ");
      return;
    }

    if (window.confirm(`ËÆÄÂèñÂà∞ ${csvData.length} Á≠ÜË≥áÊñôÔºåÊòØÂê¶‰∏äÂÇ≥Ôºü`)) {
      uploadBulk(csvData);
    }
  };

  const uploadBulk = async (rows) => {
    try {
      const payload = rows.map((row) => ({
        id_number: row["Ë∫´‰ªΩË≠âÂ≠óËôü"]?.trim(),
        name: row["ÂßìÂêç"]?.trim(),
        email: row["‰ø°ÁÆ±"]?.trim(),
        phone: row["ÈõªË©±"]?.trim(),
        department: row["ÈÉ®ÈñÄ"]?.trim(),
        title: row["ËÅ∑Á®±"]?.trim(),
        eip_account: row["EIPÂ∏≥Ëôü"]?.trim(),
      }));

      const res = await api.post("people/bulk/", payload);
      alert(`ÊàêÂäüÊñ∞Â¢û ${res.data.created} Á≠ÜÔºåÊõ¥Êñ∞ ${res.data.updated} Á≠Ü`);
      fetchPeople(departmentFilter);
    } catch (err) {
      console.error("ÂåØÂÖ•Â§±Êïó:", err.response?.data || err);
      alert("ÊâπÊ¨°ÂåØÂÖ•Â§±Êïó„ÄÇ");
    }
  };

  // üîç ÊêúÂ∞ãËàáÁØ©ÈÅ∏
  const filteredPeople = people.filter((p) => {
    const matchesSearch =
      p.name?.includes(searchTerm) ||
      p.id_number?.includes(searchTerm) ||
      p.email?.includes(searchTerm);
    const matchesDept = !departmentFilter || p.department === departmentFilter;
    return matchesSearch && matchesDept;
  });

  // üóëÔ∏è ÊâπÊ¨°Âà™Èô§
  const handleDeleteSelected = async () => {
    if (selectedIds.length === 0) {
      alert("Ë´ãÂÖàÈÅ∏ÊìáË¶ÅÂà™Èô§ÁöÑ‰∫∫Âì°„ÄÇ");
      return;
    }
    if (!window.confirm(`Á¢∫ÂÆöÂà™Èô§ ${selectedIds.length} Á≠ÜË≥áÊñôÔºü`)) return;

    try {
      const res = await api.delete("people/bulk/", {
        data: { id_numbers: selectedIds },
      });
      alert(`ÊàêÂäüÂà™Èô§ ${res.data.deleted_count} Á≠Ü`);
      setSelectedIds([]);
      fetchPeople(departmentFilter);
    } catch (err) {
      console.error("ÊâπÊ¨°Âà™Èô§Â§±Êïó:", err);
      alert("Âà™Èô§Â§±Êïó„ÄÇ");
    }
  };

  // ÊéíÂ∫è
  const handleSort = (key) => {
    setSortConfig((prev) => ({
      key,
      direction: prev.key === key && prev.direction === "asc" ? "desc" : "asc",
    }));
  };

  const sortedPeople = [...filteredPeople].sort((a, b) => {
    const valA = (a[sortConfig.key] ?? "").toString().toLowerCase();
    const valB = (b[sortConfig.key] ?? "").toString().toLowerCase();
    if (valA < valB) return sortConfig.direction === "asc" ? -1 : 1;
    if (valA > valB) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  // ÈÅ∏Âèñ
  const toggleSelect = (id) =>
    setSelectedIds((prev) =>
      prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]
    );

  const handleEdit = (p) => setEditingPerson(p);
  const cancelEdit = () => setEditingPerson(null);

  return (
    <div style={{ padding: "20px" }}>
      <h1>‰∫∫Âì°ÁÆ°ÁêÜ</h1>

      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingPerson || {}}
      />

      {editingPerson && (
        <button onClick={cancelEdit} style={{ marginBottom: "10px" }}>
          ÂèñÊ∂àÁ∑®ËºØ
        </button>
      )}

      {/* üîç ÊêúÂ∞ãËàáÁØ©ÈÅ∏ */}
      <div style={{ marginBottom: "10px" }}>
        <input
          type="text"
          placeholder="ÊêúÂ∞ãÂßìÂêç / ‰ø°ÁÆ± / Ë∫´‰ªΩË≠âÂ≠óËôü"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <select
          value={departmentFilter}
          onChange={(e) => {
            setDepartmentFilter(e.target.value);
            fetchPeople(e.target.value);
          }}
        >
          <option value="">ÂÖ®ÈÉ®ÈÉ®ÈñÄ</option>
          {departments.map((d) => (
            <option key={d} value={d}>
              {d}
            </option>
          ))}
        </select>
        {selectedIds.length > 0 && (
          <button
            onClick={handleDeleteSelected}
            style={{ marginLeft: "10px", background: "red", color: "white" }}
          >
            ÊâπÊ¨°Âà™Èô§ ({selectedIds.length})
          </button>
        )}
      </div>

      <h2>‰∫∫Âì°ÂàóË°®</h2>

      {sortedPeople.length === 0 ? (
        <p>ÁõÆÂâçÊ≤íÊúâË≥áÊñô</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>
                <input
                  type="checkbox"
                  checked={selectedIds.length === sortedPeople.length}
                  onChange={(e) =>
                    setSelectedIds(
                      e.target.checked ? sortedPeople.map((p) => p.id_number) : []
                    )
                  }
                />
              </th>
              {["name", "id_number", "department", "title", "email", "eip_account"].map(
                (key) => (
                  <th
                    key={key}
                    onClick={() => handleSort(key)}
                    style={{ cursor: "pointer" }}
                  >
                    {key === "name"
                      ? "ÂßìÂêç"
                      : key === "id_number"
                      ? "Ë∫´‰ªΩË≠âÂ≠óËôü"
                      : key === "department"
                      ? "ÈÉ®ÈñÄ"
                      : key === "title"
                      ? "ËÅ∑Á®±"
                      : key === "email"
                      ? "‰ø°ÁÆ±"
                      : "EIPÂ∏≥Ëôü"}
                    {sortConfig.key === key
                      ? sortConfig.direction === "asc"
                        ? " ‚Üë"
                        : " ‚Üì"
                      : ""}
                  </th>
                )
              )}
              <th>Êìç‰Ωú</th>
            </tr>
          </thead>
          <tbody>
            {sortedPeople.map((p, idx) => (
              <tr key={p.id_number}>
                <td>
                  <input
                    type="checkbox"
                    checked={selectedIds.includes(p.id_number)}
                    onChange={() => toggleSelect(p.id_number)}
                  />
                </td>
                <td>{p.name}</td>
                <td>{p.id_number}</td>
                <td>{p.department}</td>
                <td>{p.title}</td>
                <td>{p.email}</td>
                <td>{p.eip_account}</td>
                <td>
                  <button onClick={() => handleEdit(p)}>Á∑®ËºØ</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* È†êË¶ΩÂΩàÁ™óÔºàËã•‰Ω†Ë¶ÅÈ°ØÁ§∫ÂåØÂÖ•È†êË¶ΩÔºâ */}
      {previewData.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h3>ÂåØÂÖ•È†êË¶Ω ({previewData.length} Á≠Ü)</h3>
          <table border="1" cellPadding="3" cellSpacing="0">
            <thead>
              <tr>
                {Object.keys(previewData[0]).map((k) => (
                  <th key={k}>{k}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {previewData.slice(0, 5).map((row, idx) => (
                <tr key={idx}>
                  {Object.values(row).map((v, i) => (
                    <td key={i}>{v}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
          <p>ÔºàÂÉÖÈ°ØÁ§∫Ââç 5 Á≠ÜÔºâ</p>
        </div>
      )}
    </div>
  );
};

export default PeopleManagement;
