import React, { useState, useEffect } from "react";
import ProductList from "../components/ProductList";
import ProductForm from "../components/ProductForm";
import StockHistory from "../components/StockHistory";
import api from "../api";

export default function Inventory() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [historyProductId, setHistoryProductId] = useState(null);

  const fetchProducts = async () => {
    try {
      const res = await api.get("inventory/products/");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleEdit = (product) => setEditingProduct(product);
  const handleFormClose = () => {
    setEditingProduct(null);
    fetchProducts();
  };

  const handleViewHistory = (productId) => {
    setHistoryProductId(productId);
  };

  const handleCloseHistory = () => setHistoryProductId(null);

  return (
    <div>
      <h1>商品庫存管理</h1>
      <ProductForm product={editingProduct} onClose={handleFormClose} />
      <ProductList products={products} onEdit={handleEdit} refresh={fetchProducts} onViewHistory={handleViewHistory} />
      {historyProductId && (
        <StockHistory productId={historyProductId} onClose={handleCloseHistory} />
      )}
    </div>
  );
}
