import React, { useEffect, useState } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [editingPerson, setEditingPerson] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });

  const fields = [
    { name: "name", label: "姓名", type: "text" },
    { name: "id_number", label: "身份證字號", type: "text" },
    { name: "email", label: "Email", type: "email" },
    { name: "phone", label: "電話", type: "text" },
    { name: "department", label: "部門", type: "text" },
  ];

  // 取得人員列表
  useEffect(() => {
    const fetchPeople = async () => {
      try {
        const res = await api.get("people/");
        setPeople(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPeople();
  }, []);

    const handleAddSingle = async (person) => {
      try {
        const payload = {
          name: person.name ?? "",
          id_number: person.id_number ?? "",
          email: person.email ?? "",
          phone: person.phone ?? "",
          department: person.department ?? "",
        };
        console.log("送出單筆新增 payload:", payload);

        if (editingPerson) {
          const res = await api.put(`people/${editingPerson.id_number}/`, payload);
          console.log("更新回應:", res.data);
          setPeople((prev) =>
            prev.map((p) =>
              p.id_number === editingPerson.id_number ? res.data : p
            )
          );
          setEditingPerson(null);
        } else {
          const res = await api.post("people/", payload);
          console.log("新增回應:", res.data);
          setPeople((prev) => [...prev, res.data]);
        }
      } catch (err) {
        console.error("新增失敗錯誤:", err.response?.data || err);
        alert("新增失敗，詳細資訊請查看 console");
      }
    };

  const handleAddCSV = async (csvData) => {
    try {
      const created = [];
      for (let row of csvData) {
        const payload = {
          name: row.name,
          id_number: row.id_number,
          email: row.email,
          phone: row.phone,
          department: row.department,
        };
        const res = await api.post("people/", payload);
        created.push(res.data);
      }
      setPeople((prev) => [...prev, ...created]);
    } catch (err) {
      alert("CSV 匯入失敗");
      console.error(err);
    }
  };

  const handleDelete = async (id_number) => {
    if (!window.confirm("確定要刪除嗎？")) return;
    try {
      await api.delete(`people/${id_number}/`);
      setPeople((prev) => prev.filter((p) => p.id_number !== id_number));
    } catch (err) {
      alert("刪除失敗");
      console.error(err);
    }
  };

  const handleEdit = (person) => setEditingPerson(person);

  // 排序
  const handleSort = (key) => {
    setSortConfig((prev) => ({
      key,
      direction: prev.key === key && prev.direction === "asc" ? "desc" : "asc",
    }));
  };

  const getSortIndicator = (key) => {
    if (sortConfig.key !== key) return null;
    return sortConfig.direction === "asc" ? " ↑" : " ↓";
  };

  const sortedPeople = [...people].sort((a, b) => {
    const key = sortConfig.key;
    const valA = a[key] ?? "";
    const valB = b[key] ?? "";
    if (valA < valB) return sortConfig.direction === "asc" ? -1 : 1;
    if (valA > valB) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  return (
    <div style={{ padding: "20px" }}>
      <h1>人員管理</h1>
      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingPerson || {}}
      />

      <h2>人員列表</h2>
      {people.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th onClick={() => handleSort("name")} style={{ cursor: "pointer" }}>
                姓名{getSortIndicator("name")}
              </th>
              <th onClick={() => handleSort("id_number")} style={{ cursor: "pointer" }}>
                身份證字號{getSortIndicator("id_number")}
              </th>
              <th onClick={() => handleSort("email")} style={{ cursor: "pointer" }}>
                Email{getSortIndicator("email")}
              </th>
              <th onClick={() => handleSort("phone")} style={{ cursor: "pointer" }}>
                電話{getSortIndicator("phone")}
              </th>
              <th onClick={() => handleSort("department")} style={{ cursor: "pointer" }}>
                部門{getSortIndicator("department")}
              </th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {sortedPeople.map((p, idx) => (
              <tr key={p.id_number}>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.id_number}</td>
                <td>{p.email}</td>
                <td>{p.phone}</td>
                <td>{p.department}</td>
                <td>
                  <button onClick={() => handleEdit(p)} style={{ marginRight: "5px" }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(p.id_number)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PeopleManagement;
