import React, { useEffect, useState } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [editingPerson, setEditingPerson] = useState(null);

  const fields = [
    { name: "name", label: "Name", type: "text" },
    { name: "role", label: "Role", type: "text" }
  ];

  useEffect(() => {
    api.get("/people").then(res => setPeople(res.data));
  }, []);

  const handleSubmit = (data) => {
    if (editingPerson) {
      api.put(`/people/${editingPerson.id}`, data).then(() => setEditingPerson(null));
    } else {
      api.post("/people", data).then(() => {});
    }
  };

  return (
    <div>
      <GenericForm fields={fields} onSubmit={handleSubmit} />
      <ul>
        {people.map(p => <li key={p.id}>{p.name} - {p.role}</li>)}
      </ul>
    </div>
  );
};

export default PeopleManagement;
