import React, { useEffect, useState, useMemo } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";
import debounce from "lodash/debounce";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [editingPerson, setEditingPerson] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });
  const [filterText, setFilterText] = useState("");
  const [selectedRows, setSelectedRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const fields = [
    { name: "name", label: "姓名", type: "text" },
    { name: "id_number", label: "身份證字號", type: "text" },
    { name: "email", label: "信箱", type: "email" },
    { name: "phone", label: "電話", type: "text" },
    { name: "department", label: "部門", type: "text" },
    { name: "title", label: "職稱", type: "text" },
    { name: "eip_account", label: "EIP帳號", type: "text" },
  ];

  // === 取得人員資料 ===
  const fetchPeople = async () => {
    try {
      setLoading(true);
      const res = await api.get("people/");
      setPeople(res.data);
    } catch (err) {
      console.error("讀取人員資料失敗:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPeople();
  }, []);

  // === 單筆新增/編輯 ===
  const handleAddSingle = async (person) => {
    try {
      const payload = {
        name: person.name?.trim() ?? "",
        id_number: person.id_number?.trim() ?? "",
        email: person.email?.trim() ?? "",
        phone: person.phone?.trim() ?? "",
        department: person.department?.trim() ?? "",
        title: person.title?.trim() ?? "",
        eip_account: person.eip_account?.trim() ?? "",
      };

      let res;
      if (editingPerson) {
        res = await api.put(`people/${editingPerson.id_number}/`, payload);
        setPeople(prev =>
          prev.map(p => (p.id_number === editingPerson.id_number ? res.data : p))
        );
        setEditingPerson(null);
      } else {
        res = await api.post("people/", payload);
        setPeople(prev => [...prev, res.data]);
      }
    } catch (err) {
      console.error("新增/更新失敗:", err.response?.data || err);
      alert("操作失敗，請查看 console 詳情。");
    }
  };

  // === CSV 匯入 ===
  const handleAddCSV = async (csvData) => {
    try {
      setLoading(true);
      const res = await api.post("people/bulk/", csvData);
      await fetchPeople();
      alert(`CSV 匯入成功！共處理 ${res.data.created + res.data.updated} 筆資料。`);
    } catch (err) {
      console.error("CSV 匯入失敗:", err);
      alert("CSV 匯入失敗，請查看 console。");
    } finally {
      setLoading(false);
    }
  };

  // === 單筆刪除 ===
  const handleDelete = async (id_number) => {
    if (!window.confirm("確定要刪除此人員？")) return;
    try {
      await api.delete(`people/${id_number}/`);
      setPeople(prev => prev.filter(p => p.id_number !== id_number));
    } catch (err) {
      console.error(err);
      alert("刪除失敗");
    }
  };

  // === 批量刪除 ===
  const handleBatchDelete = async () => {
    if (selectedRows.length === 0) return alert("請先選擇要刪除的人員");
    if (!window.confirm(`確定要刪除 ${selectedRows.length} 筆資料？`)) return;
    try {
      await api.delete("people/bulk/", { data: { ids: selectedRows } });
      await fetchPeople();
      setSelectedRows([]);
      alert("批量刪除成功！");
    } catch (err) {
      console.error(err);
      alert("批量刪除失敗");
    }
  };

  const handleEdit = (person) => setEditingPerson(person);
  const cancelEdit = () => setEditingPerson(null);

  // === 排序 ===
  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === "asc" ? "desc" : "asc",
    }));
  };

  const getSortIndicator = (key) => {
    if (sortConfig.key !== key) return null;
    return sortConfig.direction === "asc" ? " ↑" : " ↓";
  };

  // === 篩選（debounce）===
  const debouncedSetFilter = useMemo(() => debounce(setFilterText, 300), []);

  const filteredPeople = useMemo(() => {
    const lower = filterText.toLowerCase();
    return people.filter(p =>
      [p.name, p.department, p.title, p.eip_account]
        .some(field => field?.toLowerCase().includes(lower))
    );
  }, [people, filterText]);

  const sortedPeople = [...filteredPeople].sort((a, b) => {
    const valA = (a[sortConfig.key] ?? "").toString().toLowerCase();
    const valB = (b[sortConfig.key] ?? "").toString().toLowerCase();
    if (valA < valB) return sortConfig.direction === "asc" ? -1 : 1;
    if (valA > valB) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  // === 選擇列 ===
  const toggleSelect = (id_number) => {
    setSelectedRows(prev =>
      prev.includes(id_number)
        ? prev.filter(id => id !== id_number)
        : [...prev, id_number]
    );
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>人員管理</h1>

      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingPerson || {}}
      />

      {editingPerson && (
        <button onClick={cancelEdit} style={{ marginBottom: "10px", backgroundColor: "#ccc" }}>
          取消編輯
        </button>
      )}

      <div style={{ marginBottom: "10px" }}>
        <input
          type="text"
          placeholder="搜尋姓名 / 部門 / 職稱 / EIP帳號"
          onChange={(e) => debouncedSetFilter(e.target.value)}
          style={{ padding: "5px", width: "300px", marginRight: "10px" }}
        />
        <button
          onClick={handleBatchDelete}
          disabled={selectedRows.length === 0}
          style={{ backgroundColor: selectedRows.length > 0 ? "red" : "#ccc", color: "#fff" }}
        >
          批量刪除
        </button>
      </div>

      <h2>人員列表</h2>
      {loading ? (
        <p>載入中...</p>
      ) : filteredPeople.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th></th>
              <th>#</th>
              <th onClick={() => handleSort("name")} style={{ cursor: "pointer" }}>
                姓名{getSortIndicator("name")}
              </th>
              <th>職稱</th>
              <th>部門</th>
              <th>EIP帳號</th>
              <th>信箱</th>
              <th>電話</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {sortedPeople.map((p, idx) => (
              <tr key={p.id_number}>
                <td>
                  <input
                    type="checkbox"
                    checked={selectedRows.includes(p.id_number)}
                    onChange={() => toggleSelect(p.id_number)}
                  />
                </td>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.title}</td>
                <td>{p.department}</td>
                <td>{p.eip_account}</td>
                <td>{p.email}</td>
                <td>{p.phone}</td>
                <td>
                  <button onClick={() => handleEdit(p)} style={{ marginRight: "5px" }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(p.id_number)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PeopleManagement;
