import React, { useState, useEffect } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [loading, setLoading] = useState(true);

  // 定義表單欄位
  const fields = [
    { name: "name", label: "姓名", type: "text" },
    { name: "email", label: "Email", type: "email" },
    { name: "role", label: "職稱", type: "text" },
  ];

  // 初始抓取舊資料
  useEffect(() => {
    const fetchPeople = async () => {
      try {
        const res = await api.get("people"); // 後端 API
        setPeople(res.data);
      } catch (err) {
        console.error("抓取人員資料失敗", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPeople();
  }, []);

  // 單筆新增
  const handleAddSingle = async (person) => {
    try {
      const res = await api.post("users/", person);
      setPeople([...people, res.data]);
    } catch (err) {
      console.error("新增人員失敗", err);
      alert(err.response?.data?.error || "新增失敗");
    }
  };

  // CSV 匯入
  const handleAddCSV = async (csvData) => {
    try {
      const added = [];
      for (const r of csvData) {
        const res = await api.post("users/", {
          name: r.name,
          email: r.email,
          role: r.role,
        });
        added.push(res.data);
      }
      setPeople([...people, ...added]);
    } catch (err) {
      console.error("CSV 匯入失敗", err);
      alert("CSV 匯入失敗");
    }
  };

  // 刪除使用者
  const handleDelete = async (id) => {
    if (!window.confirm("確定刪除嗎？")) return;
    try {
      await api.delete(`users/${id}/`);
      setPeople(people.filter((p) => p.id !== id));
    } catch (err) {
      console.error("刪除失敗", err);
      alert("刪除失敗");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>人員管理</h1>

      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
      />

      <h2>人員列表</h2>
      {people.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th>姓名</th>
              <th>Email</th>
              <th>職稱</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {people.map((p, idx) => (
              <tr key={p.id}>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.email}</td>
                <td>{p.role}</td>
                <td>
                  <button onClick={() => handleDelete(p.id)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PeopleManagement;
