import React from "react";
import ReactModal from "react-modal";

ReactModal.setAppElement("#root"); // 設定 app element 避免無障礙警告

export default function PeopleSelectModal({ candidates, onClose, onSelect }) {
  return (
    <ReactModal
      isOpen={true}
      onRequestClose={onClose}
      contentLabel="選擇持有人"
      style={{
        overlay: { backgroundColor: "rgba(0,0,0,0.5)" },
        content: { maxWidth: "400px", margin: "auto", borderRadius: "8px" },
      }}
    >
      <h2>請選擇持有人</h2>
      <ul>
        {candidates.map((p) => (
          <li key={p.id} style={{ marginBottom: "5px" }}>
            <button
              onClick={() => onSelect(p)}
              style={{ width: "100%", textAlign: "left" }}
            >
              {p.name} ({p.department})
            </button>
          </li>
        ))}
      </ul>
      <button onClick={onClose} style={{ marginTop: "10px" }}>
        取消
      </button>
    </ReactModal>
  );
}
