import React, { useState, useEffect } from "react";

const GenericForm = ({ fields, onAddSingle, onAddCSV, initialValues = {} }) => {
  const [values, setValues] = useState({});

  useEffect(() => {
    // 保證 controlled inputs
    const init = {};
    fields.forEach(f => {
      init[f.name] = initialValues[f.name] || "";
    });
    setValues(init);
  }, [initialValues, fields]);

  const handleChange = (name, value) => {
    setValues(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddSingle(values);
    setValues(fields.reduce((acc, f) => ({ ...acc, [f.name]: "" }), {}));
  };

  const handleCSV = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const text = event.target.result;
      const lines = text.split("\n").filter(Boolean);
      const headers = lines[0].split(",");
      const data = lines.slice(1).map(line => {
        const cols = line.split(",");
        const obj = {};
        headers.forEach((h, idx) => {
          obj[h.trim()] = cols[idx].trim();
        });
        return obj;
      });
      onAddCSV(data);
    };
    reader.readAsText(file);
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
      {fields.map(f => (
        <div key={f.name} style={{ marginBottom: "10px" }}>
          <label>{f.label}: </label>
          <input
            type={f.type}
            value={values[f.name] || ""}
            onChange={(e) => handleChange(f.name, e.target.value)}
          />
        </div>
      ))}
      <button type="submit">{initialValues.id ? "更新" : "新增"}</button>
      <input type="file" accept=".csv" onChange={handleCSV} style={{ marginLeft: "10px" }} />
    </form>
  );
};

export default GenericForm;
