import { useAuth } from '../context/AuthContext';
import instance from '../axios';
import { useNavigate } from 'react-router-dom';

export default function UserBar() {
  const { isAuthenticated, setIsAuthenticated } = useAuth();
  const navigate = useNavigate();

  // 從 localStorage 取得 email
  const email = localStorage.getItem('user_email') || '';
  const emailPrefix = email.split('@')[0]; // 取 @ 前面部分

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    localStorage.removeItem('user_email'); // 清掉儲存的 email
    instance.defaults.headers.common['Authorization'] = '';
    setIsAuthenticated(false);
    navigate('/login');
  };

  if (!isAuthenticated) return null;

  return (
    <div className="flex items-center justify-end space-x-4 p-2 bg-gray-100">
      <span>歡迎，{emailPrefix}</span>
      <button onClick={handleLogout} className="text-red-600 hover:underline">
        登出
      </button>
    </div>
  );
}
