import React, { useEffect, useState } from "react";
import api from "../api";

const StockTransactionPage = () => {
  const [assets, setAssets] = useState([]);
  const [people, setPeople] = useState([]);
  const [filteredPeople, setFilteredPeople] = useState([]);
  const [transactionType, setTransactionType] = useState("IN");
  const [selectedAssetTag, setSelectedAssetTag] = useState("");
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [selectedPersonId, setSelectedPersonId] = useState("");
  const [userSearch, setUserSearch] = useState("");
  const [remark, setRemark] = useState("");

  // 取得所有資產
  useEffect(() => {
    const fetchAssets = async () => {
      try {
        const res = await api.get("inventory/assets/");
        setAssets(res.data);
      } catch (err) {
        console.error(err);
        alert("載入資產失敗");
      }
    };
    fetchAssets();
  }, []);

  // 取得所有員工
  useEffect(() => {
    const fetchPeople = async () => {
      try {
        const res = await api.get("people/");
        setPeople(res.data);
        setFilteredPeople(res.data);
      } catch (err) {
        console.error(err);
        alert("載入員工失敗");
      }
    };
    fetchPeople();
  }, []);

  // 部門篩選
  useEffect(() => {
    const filtered = people.filter(
      (p) => !selectedDepartment || p.department === selectedDepartment
    );
    setFilteredPeople(
      filtered.filter(
        (p) =>
          p.name.toLowerCase().includes(userSearch.toLowerCase()) ||
          p.email.toLowerCase().includes(userSearch.toLowerCase())
      )
    );
  }, [selectedDepartment, userSearch, people]);

  // 資產依交易類型篩選
  const displayedAssets = assets.map((a) => ({
    ...a,
    disabled:
      (transactionType === "OUT" && a.holder) ||
      (transactionType === "IN" && !a.holder),
  }));

  const handleSubmit = async () => {
    if (!selectedAssetTag) {
      alert("請選擇資產");
      return;
    }
    if (transactionType === "OUT" && !selectedPersonId) {
      alert("出庫需選擇員工");
      return;
    }

    try {
      const payload = {
        asset_tag: selectedAssetTag,
        transaction_type: transactionType,
        user_id: transactionType === "OUT" ? selectedPersonId : null,
        remark,
      };
      await api.post("inventory/stock_transaction/", payload);
      alert("操作成功");

      // 重置表單
      setSelectedAssetTag("");
      setTransactionType("IN");
      setSelectedDepartment("");
      setSelectedPersonId("");
      setUserSearch("");
      setRemark("");

      // 重新載入資產列表
      const res = await api.get("inventory/assets/");
      setAssets(res.data);
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>商品出入庫</h1>

      <div style={{ marginBottom: "10px" }}>
        <label>交易類型：</label>
        <select
          value={transactionType}
          onChange={(e) => setTransactionType(e.target.value)}
        >
          <option value="IN">入庫</option>
          <option value="OUT">出庫</option>
        </select>
      </div>

      <div style={{ marginBottom: "10px" }}>
        <label>選擇資產：</label>
        <select
          value={selectedAssetTag}
          onChange={(e) => setSelectedAssetTag(e.target.value)}
        >
          <option value="">-- 請選擇資產 --</option>
          {displayedAssets.map((a) => (
            <option key={a.asset_tag} value={a.asset_tag} disabled={a.disabled}>
              {a.product_name} ({a.product_code}){" "}
              {a.disabled ? "(不可選)" : ""}
            </option>
          ))}
        </select>
      </div>

      {transactionType === "OUT" && (
        <>
          <div style={{ marginBottom: "10px" }}>
            <label>選擇部門：</label>
            <select
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
            >
              <option value="">-- 全部部門 --</option>
              {[...new Set(people.map((p) => p.department))].map((d) => (
                <option key={d} value={d}>
                  {d}
                </option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>搜尋員工：</label>
            <input
              type="text"
              placeholder="輸入姓名或Email"
              value={userSearch}
              onChange={(e) => setUserSearch(e.target.value)}
            />
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>選擇員工：</label>
            <select
              value={selectedPersonId}
              onChange={(e) => setSelectedPersonId(e.target.value)}
            >
              <option value="">-- 請選擇員工 --</option>
              {filteredPeople.map((p) => (
                <option key={p.id_number} value={p.id_number}>
                  {p.name} ({p.email})
                </option>
              ))}
            </select>
          </div>
        </>
      )}

      <div style={{ marginBottom: "10px" }}>
        <label>備註：</label>
        <input
          type="text"
          value={remark}
          onChange={(e) => setRemark(e.target.value)}
        />
      </div>

      <button onClick={handleSubmit}>提交</button>
    </div>
  );
};

export default StockTransactionPage;
