import React, { useEffect, useState } from "react";
import api from "../api";

const StockTransactionPage = () => {
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [transactionType, setTransactionType] = useState("IN");
  const [selectedAssetTag, setSelectedAssetTag] = useState("");
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [selectedUserId, setSelectedUserId] = useState("");
  const [userSearch, setUserSearch] = useState("");
  const [remark, setRemark] = useState("");

  // 取得所有資產
  const fetchAssets = async () => {
    try {
      const res = await api.get("inventory/assets/");
      setAssets(res.data);
    } catch (err) {
      console.error(err);
      alert("載入資產失敗");
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  // 取得部門列表
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await api.get("departments/");
        setDepartments(res.data);
      } catch (err) {
        console.error(err);
        alert("載入部門失敗");
      }
    };
    fetchDepartments();
  }, []);

  // 取得使用者列表（依部門）
  useEffect(() => {
    if (!selectedDepartment) {
      setUsers([]);
      setFilteredUsers([]);
      return;
    }
    const fetchUsers = async () => {
      try {
        const res = await api.get(`users/?department_id=${selectedDepartment}`);
        setUsers(res.data);
        setFilteredUsers(res.data);
      } catch (err) {
        console.error(err);
        alert("載入使用者失敗");
      }
    };
    fetchUsers();
  }, [selectedDepartment]);

  // 即時搜尋使用者
  useEffect(() => {
    const filtered = users.filter(
      (u) =>
        u.name.toLowerCase().includes(userSearch.toLowerCase()) ||
        u.email.toLowerCase().includes(userSearch.toLowerCase())
    );
    setFilteredUsers(filtered);
  }, [userSearch, users]);

  const handleSubmit = async () => {
    if (!selectedAssetTag) {
      alert("請選擇資產");
      return;
    }
    if (transactionType === "OUT" && !selectedUserId) {
      alert("出庫需選擇使用者");
      return;
    }

    try {
      const payload = {
        asset_tag: selectedAssetTag,
        transaction_type: transactionType,
        user_id: transactionType === "OUT" ? selectedUserId : null,
        remark,
      };
      await api.post("inventory/stock_transaction/", payload);
      alert("操作成功");

      // 重置表單
      setSelectedAssetTag("");
      setTransactionType("IN");
      setSelectedDepartment("");
      setSelectedUserId("");
      setUserSearch("");
      setRemark("");

      // 刷新資產列表
      fetchAssets();
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  // 篩選資產依交易類型
  const filteredAssets = assets.filter((a) => {
    if (transactionType === "OUT") return a.holder === null;
    if (transactionType === "IN") return a.holder !== null;
    return true;
  });

  return (
    <div style={{ padding: "20px" }}>
      <h1>商品出入庫</h1>

      {/* 出入庫表單 */}
      <div style={{ marginBottom: "20px", border: "1px solid #ccc", padding: "10px" }}>
        <div style={{ marginBottom: "10px" }}>
          <label>交易類型：</label>
          <select
            value={transactionType}
            onChange={(e) => setTransactionType(e.target.value)}
          >
            <option value="IN">入庫</option>
            <option value="OUT">出庫</option>
          </select>
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label>選擇資產：</label>
          <select
            value={selectedAssetTag}
            onChange={(e) => setSelectedAssetTag(e.target.value)}
          >
            <option value="">-- 請選擇資產 --</option>
            {filteredAssets.map((a) => (
              <option key={a.asset_tag} value={a.asset_tag}>
                {a.product_name} ({a.product_code}) {a.holder ? `- ${a.holder.name}` : ""}
              </option>
            ))}
          </select>
        </div>

        {transactionType === "OUT" && (
          <>
            <div style={{ marginBottom: "10px" }}>
              <label>選擇部門：</label>
              <select
                value={selectedDepartment}
                onChange={(e) => setSelectedDepartment(e.target.value)}
              >
                <option value="">-- 請選擇部門 --</option>
                {departments.map((d) => (
                  <option key={d.id} value={d.id}>
                    {d.name}
                  </option>
                ))}
              </select>
            </div>

            <div style={{ marginBottom: "10px" }}>
              <label>搜尋使用者：</label>
              <input
                type="text"
                placeholder="輸入姓名或Email"
                value={userSearch}
                onChange={(e) => setUserSearch(e.target.value)}
              />
            </div>

            <div style={{ marginBottom: "10px" }}>
              <label>選擇使用者：</label>
              <select
                value={selectedUserId}
                onChange={(e) => setSelectedUserId(e.target.value)}
              >
                <option value="">-- 請選擇使用者 --</option>
                {filteredUsers.map((u) => (
                  <option key={u.id} value={u.id}>
                    {u.name} ({u.email})
                  </option>
                ))}
              </select>
            </div>
          </>
        )}

        <div style={{ marginBottom: "10px" }}>
          <label>備註：</label>
          <input
            type="text"
            value={remark}
            onChange={(e) => setRemark(e.target.value)}
          />
        </div>

        <button onClick={handleSubmit}>提交</button>
      </div>

      {/* 資產列表 */}
      <h2>資產列表</h2>
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>#</th>
            <th>資產名稱</th>
            <th>財產編號</th>
            <th>種類</th>
            <th>價格</th>
            <th>持有人</th>
          </tr>
        </thead>
        <tbody>
          {assets.map((a, idx) => (
            <tr key={a.asset_tag}>
              <td>{idx + 1}</td>
              <td>{a.product_name}</td>
              <td>{a.product_code}</td>
              <td>{a.type}</td>
              <td>{a.price}</td>
              <td>{a.holder ? a.holder.name : "無"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTransactionPage;
