import React, { useEffect, useState } from "react";
import api from "../api";

const StockTransactionPage = () => {
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [people, setPeople] = useState([]); // 所有 people（同部門才顯示）
  const [filteredPeople, setFilteredPeople] = useState([]);

  const [selectedAssetId, setSelectedAssetId] = useState(""); // 存 asset_tag（字串）
  const [transactionType, setTransactionType] = useState("IN");
  const [selectedDepartment, setSelectedDepartment] = useState(""); // 存 department id（數字或字串視 API）
  const [selectedPersonId, setSelectedPersonId] = useState(""); // 存 person.id (數字或字串)
  const [personSearch, setPersonSearch] = useState("");
  const [remark, setRemark] = useState("");

  // 1) 取得資產（Asset 為單位）
  const fetchAssets = async () => {
    try {
      const res = await api.get("inventory/assets/");
      setAssets(res.data || []);
    } catch (err) {
      console.error(err);
      alert("載入資產失敗");
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  // 2) 取得部門
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await api.get("departments/");
        setDepartments(res.data || []);
      } catch (err) {
        console.error(err);
        alert("載入部門失敗");
      }
    };
    fetchDepartments();
  }, []);

  // 3) 取得 people（全部或依部門 api）
  //    我們先載入全部 people（若人數很多，可改成依部門呼叫後端）
  useEffect(() => {
    const fetchPeople = async () => {
      try {
        const res = await api.get("people/");
        setPeople(res.data || []);
        setFilteredPeople(res.data || []);
      } catch (err) {
        console.error(err);
        alert("載入員工失敗");
      }
    };
    fetchPeople();
  }, []);

  // 4) 當選擇部門或搜尋字串改變時，在前端做過濾（確保不依賴後端 query）
  useEffect(() => {
    let list = people;
    if (selectedDepartment) {
      // 部門比對：假設 people 的 department 欄位是 department_id 或 department.name
      // 我們先嘗試 department_id 比對，如果沒有就 fallback 比對 department name
      list = list.filter((p) => {
        if (p.department_id !== undefined && p.department_id !== null) {
          return String(p.department_id) === String(selectedDepartment);
        }
        // fallback to name
        return String(p.department) === String(
          // find department name from departments array
          (departments.find((d) => String(d.id) === String(selectedDepartment)) || {}).name
        );
      });
    }
    if (personSearch) {
      const q = personSearch.toLowerCase();
      list = list.filter(
        (p) =>
          (p.name && p.name.toLowerCase().includes(q)) ||
          (p.id_number && p.id_number.toLowerCase().includes(q)) ||
          (p.email && p.email.toLowerCase().includes(q))
      );
    }
    setFilteredPeople(list);
  }, [selectedDepartment, personSearch, people, departments]);

  // 5) 根據交易類型篩選可選資產（直接 filter 出來，不用 disabled）
  const availableAssets = assets.filter((a) =>
    transactionType === "OUT" ? (a.holder === null || a.holder === undefined) : (a.holder !== null && a.holder !== undefined)
  );

  // 6) 如果目前被選的 asset 不在 availableAssets（例如切換 IN/OUT），就清空選擇（避免不一致但也避免“閃回”循環）
  useEffect(() => {
    if (selectedAssetId) {
      const exists = availableAssets.some((a) => a.asset_tag === selectedAssetId);
      if (!exists) {
        setSelectedAssetId("");
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [transactionType, assets]); // 當 assets 或 transactionType 改變時檢查

  // 提交
  const handleSubmit = async () => {
    if (!selectedAssetId) {
      alert("請選擇資產");
      return;
    }
    if (transactionType === "OUT" && !selectedPersonId) {
      alert("出庫請選擇員工");
      return;
    }

    try {
      const payload = {
        asset_tag: selectedAssetId,
        transaction_type: transactionType,
        // 後端若期待 employee id，傳 selectedPersonId（此為 people API 回傳的 id）
        employee_id: transactionType === "OUT" ? selectedPersonId : null,
        remark,
      };
      await api.post("inventory/stock_transaction/", payload);
      alert("操作成功");
      // reset
      setSelectedAssetId("");
      setTransactionType("IN");
      setSelectedDepartment("");
      setSelectedPersonId("");
      setPersonSearch("");
      setRemark("");
      fetchAssets();
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>商品出入庫</h1>

      {/* 交易類型（放第一） */}
      <div style={{ marginBottom: 12 }}>
        <label style={{ marginRight: 8 }}>交易類型：</label>
        <select value={transactionType} onChange={(e) => setTransactionType(e.target.value)}>
          <option value="IN">入庫</option>
          <option value="OUT">出庫</option>
        </select>
      </div>

      {/* 選擇資產（只列 availableAssets） */}
      <div style={{ marginBottom: 12 }}>
        <label style={{ marginRight: 8 }}>選擇資產：</label>
        <select value={selectedAssetId} onChange={(e) => setSelectedAssetId(e.target.value)}>
          <option value="">-- 請選擇資產 --</option>
          {availableAssets.map((a) => (
            <option key={a.asset_tag} value={a.asset_tag}>
              {a.product_name} ({a.product_code}) {a.holder ? `- ${a.holder.name}` : "- 在庫"}
            </option>
          ))}
        </select>
      </div>

      {/* 出庫時顯示部門 + 搜尋 + 員工清單 */}
      {transactionType === "OUT" && (
        <>
          <div style={{ marginBottom: 12 }}>
            <label style={{ marginRight: 8 }}>選擇部門：</label>
            <select value={selectedDepartment} onChange={(e) => setSelectedDepartment(e.target.value)}>
              <option value="">-- 全部部門 --</option>
              {departments.map((d) => (
                <option key={d.id} value={d.id}>
                  {d.name}
                </option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: 12 }}>
            <label style={{ marginRight: 8 }}>搜尋員工：</label>
            <input
              type="text"
              placeholder="姓名 / 員工編號 / email"
              value={personSearch}
              onChange={(e) => setPersonSearch(e.target.value)}
            />
          </div>

          <div style={{ marginBottom: 12 }}>
            <label style={{ marginRight: 8 }}>選擇員工：</label>
            <select value={selectedPersonId} onChange={(e) => setSelectedPersonId(e.target.value)}>
              <option value="">-- 請選擇員工 --</option>
              {filteredPeople.map((p) => (
                <option key={p.id} value={p.id}>
                  {p.name} ({p.id_number || p.email || p.id})
                </option>
              ))}
            </select>
          </div>
        </>
      )}

      <div style={{ marginBottom: 12 }}>
        <label style={{ marginRight: 8 }}>備註：</label>
        <input value={remark} onChange={(e) => setRemark(e.target.value)} />
      </div>

      <div>
        <button onClick={handleSubmit}>提交</button>
      </div>

      {/* 資產列表（參考用） */}
      <h2 style={{ marginTop: 24 }}>資產列表（示意）</h2>
      <table border="1" cellPadding="6">
        <thead>
          <tr>
            <th>#</th>
            <th>產品</th>
            <th>財產編號</th>
            <th>種類</th>
            <th>價格</th>
            <th>持有人</th>
          </tr>
        </thead>
        <tbody>
          {assets.map((a, idx) => (
            <tr key={a.asset_tag}>
              <td>{idx + 1}</td>
              <td>{a.product_name}</td>
              <td>{a.product_code}</td>
              <td>{a.type}</td>
              <td>{a.price}</td>
              <td>{a.holder ? a.holder.name : "無"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTransactionPage;
