import { useState, useEffect } from "react";
import axios from "../api"; // 你的自訂 Axios
import ProductForm from "../components/ProductForm";
import Modal from "../components/Modal"; // 你可以用任何 Modal 或自己寫一個簡單版

export default function Inventory() {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const fetchProducts = async () => {
    try {
      const res = await axios.get("inventory/products/");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddSuccess = () => {
    setShowModal(false);
    fetchProducts(); // 新增成功後刷新列表
  };

  return (
    <div>
      <h1>庫存管理</h1>
      <button onClick={() => setShowModal(true)}>新增商品</button>

      {showModal && (
        <Modal onClose={() => setShowModal(false)}>
          <ProductForm onSuccess={handleAddSuccess} />
        </Modal>
      )}

      <table>
        <thead>
          <tr>
            <th>名稱</th>
            <th>代碼</th>
            <th>單位</th>
            <th>價格</th>
            <th>庫存</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>{p.code}</td>
              <td>{p.unit}</td>
              <td>{p.price}</td>
              <td>{p.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
