import React, { useEffect, useState } from "react";
import axios from "../api"; // 你自訂的 axios instance

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [selected, setSelected] = useState([]);
  const [search, setSearch] = useState("");
  const [departmentFilter, setDepartmentFilter] = useState("");
  const [transferDept, setTransferDept] = useState(""); // 批量轉部門的目標部門

  // 取得人員列表
  const fetchPeople = async () => {
    try {
      const params = {};
      if (search) params.name = search;
      if (departmentFilter) params.department = departmentFilter;

      const res = await axios.get("/users/", { params });
      setPeople(res.data);
    } catch (err) {
      console.error(err);
      alert("取得人員列表失敗");
    }
  };

  useEffect(() => {
    fetchPeople();
  }, []);

  // 勾選/取消勾選
  const toggleSelect = (id_number) => {
    setSelected((prev) =>
      prev.includes(id_number)
        ? prev.filter((id) => id !== id_number)
        : [...prev, id_number]
    );
  };

  // 批量操作
  const handleBatchAction = async (action) => {
    if (!selected.length) return alert("請先勾選人員");
    if (action === "leave" && !window.confirm("確定將選中的人員設為離職？")) return;
    if (action === "delete" && !window.confirm("確定刪除選中的人員？")) return;
    if (action === "transfer" && !transferDept) return alert("請輸入目標部門");

    try {
      const payload = selected.map((id_number) => ({
        id_number,
        action,
        new_department: action === "transfer" ? transferDept : undefined,
      }));

      await axios.post("/users/batch/", payload);
      alert("操作成功");
      fetchPeople();
      setSelected([]);
      setTransferDept("");
    } catch (err) {
      console.error(err);
      alert("批量操作失敗");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>人員管理</h2>

      <div style={{ marginBottom: "10px" }}>
        <input
          placeholder="搜尋姓名"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          placeholder="部門篩選"
          value={departmentFilter}
          onChange={(e) => setDepartmentFilter(e.target.value)}
        />
        <button onClick={fetchPeople}>搜尋</button>
      </div>

      <table border="1" cellPadding="5" style={{ width: "100%" }}>
        <thead>
          <tr>
            <th>
              <input
                type="checkbox"
                checked={selected.length === people.length && people.length > 0}
                onChange={(e) =>
                  setSelected(e.target.checked ? people.map(p => p.id_number) : [])
                }
              />
            </th>
            <th>EIP帳號</th>
            <th>姓名</th>
            <th>部門名稱</th>
            <th>身份證字號</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {people.map((p) => (
            <tr key={p.id_number}>
              <td>
                <input
                  type="checkbox"
                  checked={selected.includes(p.id_number)}
                  onChange={() => toggleSelect(p.id_number)}
                />
              </td>
              <td>{p.eip_account}</td>
              <td>{p.name}</td>
              <td>{p.department || "-"}</td>
              <td>{p.id_number}</td>
              <td>
                <button onClick={() => alert("編輯功能待實作")}>編輯</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div style={{ marginTop: "10px" }}>
        <button onClick={() => handleBatchAction("leave")}>批量離職</button>
        <input
          placeholder="轉換部門"
          value={transferDept}
          onChange={(e) => setTransferDept(e.target.value)}
          style={{ marginLeft: "10px" }}
        />
        <button onClick={() => handleBatchAction("transfer")}>批量轉部門</button>
        <button onClick={() => handleBatchAction("delete")}>批量刪除</button>
      </div>
    </div>
  );
};

export default PeopleManagement;
