import React, { useState, useEffect } from "react";
import GenericForm from "../components/GenericForm";

const Inventory = () => {
  const [products, setProducts] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });
  const [detailedMode, setDetailedMode] = useState(
    JSON.parse(localStorage.getItem("inventoryDetailedMode")) ?? false
  );

  const simpleFields = [
    { name: "name", label: "名稱", type: "text" },
    { name: "price", label: "價格", type: "number" },
    { name: "quantity", label: "數量", type: "number" },
  ];

  const detailedFields = [
    { name: "name", label: "名稱", type: "text" },
    { name: "asset_number", label: "財產編號", type: "text" },
    { name: "category", label: "種類", type: "text" },
    { name: "price", label: "價格", type: "number" },
    { name: "quantity", label: "數量", type: "number" },
    { name: "holder", label: "持有人", type: "text" },
    { name: "borrowed_at", label: "借用時間", type: "text" },
    { name: "returned_at", label: "歸還時間", type: "text" },
  ];

  const fields = detailedMode ? detailedFields : simpleFields;

  useEffect(() => {
    localStorage.setItem("inventoryDetailedMode", JSON.stringify(detailedMode));
  }, [detailedMode]);

  const handleAddSingle = (product) => {
    setProducts((prev) => [...prev, product]);
  };

  const handleAddCSV = (csvData) => {
    if (!csvData) return;
    const formatted = csvData.map((row) => {
      const obj = {};
      fields.forEach((f) => {
        obj[f.name] = row[f.name] ?? "";
      });
      return obj;
    });
    setProducts((prev) => [...prev, ...formatted]);
  };

  const handleDelete = (index) => {
    setProducts((prev) => prev.filter((_, idx) => idx !== index));
  };

  const handleSort = (key) => {
    setSortConfig((prev) => ({
      key,
      direction: prev.key === key && prev.direction === "asc" ? "desc" : "asc",
    }));
  };

  const getSortIndicator = (key) => {
    if (sortConfig.key !== key) return null;
    return sortConfig.direction === "asc" ? " ↑" : " ↓";
  };

  const sortedProducts = [...products].sort((a, b) => {
    const valA = a[sortConfig.key] ?? "";
    const valB = b[sortConfig.key] ?? "";
    if (valA < valB) return sortConfig.direction === "asc" ? -1 : 1;
    if (valA > valB) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>

      <div style={{ marginBottom: "10px" }}>
        <button
          type="button"
          onClick={() => setDetailedMode(false)}
          style={{ marginRight: "10px", background: !detailedMode ? "#007bff" : "#ccc", color: "#fff" }}
        >
          簡略模式
        </button>
        <button
          type="button"
          onClick={() => setDetailedMode(true)}
          style={{ background: detailedMode ? "#007bff" : "#ccc", color: "#fff" }}
        >
          詳細模式
        </button>
      </div>

      <GenericForm fields={fields} onAddSingle={handleAddSingle} onAddCSV={handleAddCSV} />

      <h2>產品列表</h2>
      {products.length === 0 ? (
        <p>目前沒有產品</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              {fields.map((f) => (
                <th
                  key={f.name}
                  onClick={() => handleSort(f.name)}
                  style={{ cursor: "pointer" }}
                >
                  {f.label}
                  {getSortIndicator(f.name)}
                </th>
              ))}
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {sortedProducts.map((p, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                {fields.map((f) => (
                  <td key={f.name}>{p[f.name] ?? ""}</td>
                ))}
                <td>
                  <button onClick={() => alert("編輯功能待實作")} style={{ marginRight: "5px" }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(idx)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Inventory;
