import React from "react";
import api from "../api";

export default function ProductList({ products, onEdit, refresh }) {
  const handleTransaction = async (product, type) => {
    const qty = parseInt(prompt("輸入數量："), 10);
    if (!qty || qty <= 0) return;

    try {
      await api.post(`inventory/products/${product.id}/transaction/`, {
        transaction_type: type,
        quantity: qty,
      });
      refresh();
    } catch (err) {
      alert(err.response?.data?.error || "操作失敗");
    }
  };

  const viewHistory = async (product) => {
    try {
      const res = await api.get(`inventory/products/${product.id}/history/`);
      alert(JSON.stringify(res.data, null, 2));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <table border="1" cellPadding="5">
      <thead>
        <tr>
          <th>名稱</th>
          <th>代碼</th>
          <th>單位</th>
          <th>價格</th>
          <th>庫存</th>
          <th>操作</th>
        </tr>
      </thead>
      <tbody>
        {products.map((p) => (
          <tr key={p.id}>
            <td>{p.name}</td>
            <td>{p.code}</td>
            <td>{p.unit}</td>
            <td>{p.price}</td>
            <td>{p.quantity}</td>
            <td>
              <button onClick={() => onEdit(p)}>編輯</button>{" "}
              <button onClick={() => handleTransaction(p, "IN")}>入庫</button>{" "}
              <button onClick={() => handleTransaction(p, "OUT")}>出庫</button>{" "}
              <button onClick={() => viewHistory(p)}>庫存歷史</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
