import { Link } from 'react-router-dom';
import { Users, Boxes, Truck, PackagePlus, BarChart2, Settings } from 'lucide-react';

const features = [
  { name: '人員管理', icon: Users, path: '/people' },
  { name: '商品庫存管理', icon: Boxes, path: '/inventory' },
  { name: '商品出庫', icon: Truck, path: '/inventory' },
  { name: '商品入庫', icon: PackagePlus, path: '/inventory' },
  { name: '報表', icon: BarChart2, path: '/report' },
  { name: '設定', icon: Settings, path: '/settings' },
];

export default function Home() {
  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <h1 style={{ fontSize: '2rem', marginBottom: '2rem' }}>設備與庫存管理系統</h1>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(140px, 1fr))', gap: '1rem', width: '80%', maxWidth: '800px' }}>
        {features.map((item) => {
          const Icon = item.icon;
          return (
            <Link key={item.name} to={item.path} style={{ textDecoration: 'none', padding: '1rem', background: '#fff', borderRadius: '12px', boxShadow: '0 2px 8px rgba(0,0,0,0.15)', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
              <Icon size={36} color="#2563EB" style={{ marginBottom: '0.5rem' }} />
              <span style={{ fontSize: '1rem', fontWeight: '500', color: '#111' }}>{item.name}</span>
            </Link>
          );
        })}
      </div>
    </div>
  );
}
