import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import debounce from "lodash/debounce";

const GenericForm = ({ fields, onAddSingle, onAddCSV, initialValues, onOwnerInput }) => {
  const [mode, setMode] = useState("single");
  const initialState = fields.reduce(
    (acc, f) => ({ ...acc, [f.name]: "" }),
    {}
  );

  const [formData, setFormData] = useState(() => ({ ...initialState, ...initialValues }));
  const [csvFile, setCsvFile] = useState(null);
  const [isComposing, setIsComposing] = useState(false);

  useEffect(() => {
    setFormData({ ...initialState, ...initialValues });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialValues]);

  const handleModeChange = (newMode) => {
    setMode(newMode);
    setFormData({ ...initialState, ...initialValues });
    setCsvFile(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));

    // 只對 owner_user 執行搜尋
    if (name === "owner_user" && onOwnerInput && !isComposing) {
      debouncedOwnerInput(value);
    }
  };

  // debounce 搜尋函數，500ms 後才觸發
  const debouncedOwnerInput = debounce((name) => {
    if (name.trim() === "") return;
    onOwnerInput(name, (selectedId) => {
      setFormData((prev) => ({ ...prev, owner_user: selectedId }));
    });
  }, 500);

  const handleCompositionStart = () => setIsComposing(true);
  const handleCompositionEnd = (e) => {
    setIsComposing(false);
    // 完成輸入後立即觸發一次搜尋
    handleInputChange(e);
  };

  const handleSingleSubmit = (e) => {
    e.preventDefault();
    onAddSingle(formData);
    setFormData({ ...initialState, ...initialValues });
  };

  const handleCSVChange = (e) => setCsvFile(e.target.files[0]);

  const handleCSVSubmit = (e) => {
    e.preventDefault();
    if (!csvFile) return;
    Papa.parse(csvFile, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        onAddCSV(results.data);
        setCsvFile(null);
      },
    });
  };

  const isEditing = !!initialValues?.id;

  return (
    <div style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "20px" }}>
      <div style={{ marginBottom: "10px" }}>
        <button
          type="button"
          onClick={() => handleModeChange("single")}
          style={{
            marginRight: "10px",
            background: mode === "single" ? "#007bff" : "#ccc",
            color: "#fff",
          }}
        >
          單筆新增
        </button>
        <button
          type="button"
          onClick={() => handleModeChange("csv")}
          style={{
            background: mode === "csv" ? "#007bff" : "#ccc",
            color: "#fff",
          }}
        >
          CSV 匯入
        </button>
      </div>

      {mode === "single" && (
        <form onSubmit={handleSingleSubmit}>
          {fields.map((f) => {
            if ((f.name === "borrowed_at" || f.name === "returned_at") && !formData.holder) return null;
            const isRequired = f.required && !(f.name === "borrowed_at" || f.name === "returned_at");

            // 特殊處理 owner_user 欄位
            if (f.name === "owner_user") {
              return (
                <input
                  key={f.name}
                  type="text"
                  name={f.name}
                  placeholder={f.label}
                  value={formData[f.name] || ""}
                  onChange={handleInputChange}
                  onCompositionStart={handleCompositionStart}
                  onCompositionEnd={handleCompositionEnd}
                  required={isRequired}
                  style={{ marginRight: "10px", marginBottom: "5px" }}
                />
              );
            }

            return (
              <input
                key={f.name}
                type={f.type}
                name={f.name}
                placeholder={f.label}
                value={formData[f.name] || ""}
                onChange={handleInputChange}
                required={isRequired}
                style={{ marginRight: "10px", marginBottom: "5px" }}
              />
            );
          })}
          <button type="submit">
            {isEditing ? "更新" : "新增"}
          </button>
        </form>
      )}

      {mode === "csv" && (
        <form onSubmit={handleCSVSubmit}>
          <input
            type="file"
            accept=".csv"
            onChange={handleCSVChange}
            style={{ marginRight: "10px" }}
          />
          <button type="submit">匯入 CSV</button>
        </form>
      )}
    </div>
  );
};

export default GenericForm;
