import React, { useEffect, useState } from "react";
import api from "../api";

const StockTransactionPage = () => {
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [people, setPeople] = useState([]);
  const [filteredPeople, setFilteredPeople] = useState([]);

  const [selectedAssetTag, setSelectedAssetTag] = useState("");
  const [transactionType, setTransactionType] = useState("IN");
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [selectedPersonId, setSelectedPersonId] = useState("");
  const [personSearch, setPersonSearch] = useState("");
  const [remark, setRemark] = useState("");

  // 取得所有資產
  const fetchAssets = async () => {
    try {
      const res = await api.get("inventory/assets/");
      setAssets(res.data);
    } catch (err) {
      console.error(err);
      alert("載入資產失敗");
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  // 取得部門列表
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await api.get("departments/");
        setDepartments(res.data);
      } catch (err) {
        console.error(err);
        alert("載入部門失敗");
      }
    };
    fetchDepartments();
  }, []);

  // 取得人員列表（依部門）
  useEffect(() => {
    if (!selectedDepartment) {
      setPeople([]);
      setFilteredPeople([]);
      return;
    }
    const fetchPeople = async () => {
      try {
        const res = await api.get(`people/?department=${encodeURIComponent(selectedDepartment)}`);
        setPeople(res.data);
        setFilteredPeople(res.data);
      } catch (err) {
        console.error(err);
        alert("載入人員失敗");
      }
    };
    fetchPeople();
  }, [selectedDepartment]);

  // 即時搜尋人員
  useEffect(() => {
    const filtered = people.filter(
      (p) =>
        p.name.toLowerCase().includes(personSearch.toLowerCase()) ||
        p.email.toLowerCase().includes(personSearch.toLowerCase())
    );
    setFilteredPeople(filtered);
  }, [personSearch, people]);

  // 根據交易類型篩選顯示的資產
  const displayedAssets = assets.filter((a) => {
    if (transactionType === "OUT") return !a.holder; // 庫存內才可出庫
    if (transactionType === "IN") return a.holder; // 在人手上才可入庫
    return true;
  });

  const handleSubmit = async () => {
    if (!selectedAssetTag) {
      alert("請選擇資產");
      return;
    }
    if (transactionType === "OUT" && !selectedPersonId) {
      alert("出庫需選擇人員");
      return;
    }

    try {
      const payload = {
        asset_tag: selectedAssetTag,
        transaction_type: transactionType,
        person_id: transactionType === "OUT" ? selectedPersonId : null,
        remark,
      };
      await api.post("inventory/stock_transaction/", payload);
      alert("操作成功");

      // 重置表單
      setSelectedAssetTag("");
      setTransactionType("IN");
      setSelectedDepartment("");
      setSelectedPersonId("");
      setPersonSearch("");
      setRemark("");

      fetchAssets();
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>商品出入庫</h1>

      <div style={{ marginBottom: "10px" }}>
        <label>交易類型：</label>
        <select
          value={transactionType}
          onChange={(e) => setTransactionType(e.target.value)}
        >
          <option value="IN">入庫</option>
          <option value="OUT">出庫</option>
        </select>
      </div>

      <div style={{ marginBottom: "10px" }}>
        <label>選擇資產：</label>
        <select
          value={selectedAssetTag}
          onChange={(e) => setSelectedAssetTag(e.target.value)}
        >
          <option value="">請選擇資產</option>
          {displayedAssets.map((a) => (
            <option key={a.asset_tag} value={a.asset_tag}>
              {a.product_name} ({a.product_code}) {a.holder ? `(持有人: ${a.holder.name})` : ""}
            </option>
          ))}
        </select>
      </div>

      {transactionType === "OUT" && (
        <>
          <div style={{ marginBottom: "10px" }}>
            <label>選擇部門：</label>
            <select
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
            >
              <option value="">-- 請選擇部門 --</option>
              {departments.map((d) => (
                <option key={d.id} value={d.name}>
                  {d.name}
                </option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>搜尋人員：</label>
            <input
              type="text"
              placeholder="輸入姓名或Email"
              value={personSearch}
              onChange={(e) => setPersonSearch(e.target.value)}
            />
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>選擇人員：</label>
            <select
              value={selectedPersonId}
              onChange={(e) => setSelectedPersonId(e.target.value)}
            >
              <option value="">-- 請選擇人員 --</option>
              {filteredPeople.map((p) => (
                <option key={p.id_number} value={p.id_number}>
                  {p.name} ({p.email})
                </option>
              ))}
            </select>
          </div>
        </>
      )}

      <div style={{ marginBottom: "10px" }}>
        <label>備註：</label>
        <input
          type="text"
          value={remark}
          onChange={(e) => setRemark(e.target.value)}
        />
      </div>

      <button onClick={handleSubmit}>提交</button>

      <h2 style={{ marginTop: "20px" }}>資產列表（自動刷新）</h2>
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>#</th>
            <th>名稱</th>
            <th>財產編號</th>
            <th>種類</th>
            <th>價格</th>
            <th>持有人</th>
          </tr>
        </thead>
        <tbody>
          {assets.map((a, idx) => (
            <tr key={a.asset_tag}>
              <td>{idx + 1}</td>
              <td>{a.product_name}</td>
              <td>{a.product_code}</td>
              <td>{a.type}</td>
              <td>{a.price}</td>
              <td>{a.holder ? a.holder.name : "無"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTransactionPage;
