import React, { useState } from "react";
import Papa from "papaparse";

const ProductForm = ({ onAddSingle, onAddCSV }) => {
  const [mode, setMode] = useState("single"); // single 或 csv
  const [product, setProduct] = useState({ name: "", price: "", quantity: "" });
  const [csvFile, setCsvFile] = useState(null);

  // 切換輸入模式
  const handleModeChange = (newMode) => {
    setMode(newMode);
    // 重置輸入
    setProduct({ name: "", price: "", quantity: "" });
    setCsvFile(null);
  };

  // 單筆新增表單輸入
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleSingleSubmit = (e) => {
    e.preventDefault();
    onAddSingle(product);
    setProduct({ name: "", price: "", quantity: "" });
  };

  // CSV 匯入
  const handleCSVChange = (e) => {
    setCsvFile(e.target.files[0]);
  };

  const handleCSVSubmit = (e) => {
    e.preventDefault();
    if (!csvFile) return;

    Papa.parse(csvFile, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        onAddCSV(results.data);
        setCsvFile(null);
      },
    });
  };

  return (
    <div style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "20px" }}>
      <div style={{ marginBottom: "10px" }}>
        <button
          type="button"
          onClick={() => handleModeChange("single")}
          style={{ marginRight: "10px", background: mode === "single" ? "#007bff" : "#ccc", color: "#fff" }}
        >
          單筆新增
        </button>
        <button
          type="button"
          onClick={() => handleModeChange("csv")}
          style={{ background: mode === "csv" ? "#007bff" : "#ccc", color: "#fff" }}
        >
          CSV 匯入
        </button>
      </div>

      {mode === "single" && (
        <form onSubmit={handleSingleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="名稱"
            value={product.name}
            onChange={handleInputChange}
            required
            style={{ marginRight: "10px" }}
          />
          <input
            type="number"
            name="price"
            placeholder="價格"
            value={product.price}
            onChange={handleInputChange}
            required
            style={{ marginRight: "10px" }}
          />
          <input
            type="number"
            name="quantity"
            placeholder="數量"
            value={product.quantity}
            onChange={handleInputChange}
            required
            style={{ marginRight: "10px" }}
          />
          <button type="submit">新增</button>
        </form>
      )}

      {mode === "csv" && (
        <form onSubmit={handleCSVSubmit}>
          <input type="file" accept=".csv" onChange={handleCSVChange} style={{ marginRight: "10px" }} />
          <button type="submit">匯入 CSV</button>
        </form>
      )}
    </div>
  );
};

export default ProductForm;
