import React, { useState, useEffect } from "react";
import api from "../api";

export default function ProductForm({ product, onClose }) {
  const [form, setForm] = useState({
    name: "",
    code: "",
    unit: "",
    price: "",
    quantity: "",
  });

  useEffect(() => {
    if (product) {
      setForm({
        name: product.name || "",
        code: product.code || "",
        unit: product.unit || "",
        price: product.price || "",
        quantity: product.quantity || "",
      });
    }
  }, [product]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (product) {
        await api.put(`inventory/products/${product.id}/`, form);
      } else {
        await api.post("inventory/products/", form);
      }
      onClose();
    } catch (err) {
      alert(err.response?.data?.error || "操作失敗");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="name" placeholder="名稱" value={form.name} onChange={handleChange} required />
      <input name="code" placeholder="代碼" value={form.code} onChange={handleChange} />
      <input name="unit" placeholder="單位" value={form.unit} onChange={handleChange} />
      <input name="price" placeholder="價格" value={form.price} onChange={handleChange} />
      <input name="quantity" placeholder="庫存" value={form.quantity} onChange={handleChange} />
      <button type="submit">{product ? "更新" : "新增"}</button>
      {product && <button type="button" onClick={onClose}>取消</button>}
    </form>
  );
}
