import React, { useEffect, useState } from "react";
import api from "../api"; // 你既有的 axios 包裝

const Settings = () => {
  const [duplicateCheck, setDuplicateCheck] = useState(false);
  const [loading, setLoading] = useState(true);

  // 取得目前設定
  useEffect(() => {
    api.get("/system/check-setting/")
      .then(res => {
        setDuplicateCheck(res.data.enabled);
      })
      .catch(err => {
        console.error("載入設定失敗：", err);
      })
      .finally(() => setLoading(false));
  }, []);

  // 切換設定
  const handleToggle = async () => {
    const newValue = !duplicateCheck;
    try {
      await api.post("/system/check-setting/", { enabled: newValue });
      setDuplicateCheck(newValue);
    } catch (err) {
      console.error("更新設定失敗：", err);
      alert("更新設定失敗！");
    }
  };

  if (loading) return <div>載入中...</div>;

  return (
    <div className="p-6">
      <h2 className="text-xl font-semibold mb-4">系統設定</h2>

      <div className="flex items-center gap-3">
        <label className="text-lg">產品重複檢查</label>
        <button
          className={`px-4 py-2 rounded-lg text-white transition-colors ${
            duplicateCheck ? "bg-green-600 hover:bg-green-700" : "bg-gray-500 hover:bg-gray-600"
          }`}
          onClick={handleToggle}
        >
          {duplicateCheck ? "已啟用" : "已停用"}
        </button>
      </div>
    </div>
  );
};

export default Settings;
