import React, { useEffect, useState } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const Inventory = () => {
  const [assets, setAssets] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });
  const [detailedMode, setDetailedMode] = useState(
    localStorage.getItem("inventoryDetailedMode") === "true"
  );

  const fields = [
    { name: "name", label: "名稱", type: "text" },
    { name: "code", label: "財產編號", type: "text" },
    { name: "type", label: "種類", type: "text" },
    { name: "price", label: "價格", type: "number" },
    { name: "owner_user", label: "持有人", type: "text" },
  ];

  useEffect(() => {
    const fetchAssets = async () => {
      try {
        const res = await api.get("inventory/assets/");
        setAssets(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAssets();
  }, []);

  // 下拉選單用 owner 搜尋
  const handleOwnerInput = async (keyword) => {
    try {
      const res = await api.get(`people/search/?name=${encodeURIComponent(keyword)}`);
      return res.data; // GenericForm 會處理下拉選單
    } catch (err) {
      console.error(err);
      return [];
    }
  };

  const handleSort = (key) => {
    setSortConfig((prev) => {
      const direction = prev.key === key && prev.direction === "asc" ? "desc" : "asc";
      return { key, direction };
    });
  };

  const sortedAssets = [...assets].sort((a, b) => {
    let aVal, bVal;
    switch (sortConfig.key) {
      case "name":
        aVal = a.product?.name ?? "";
        bVal = b.product?.name ?? "";
        break;
      case "code":
        aVal = a.asset_tag ?? "";
        bVal = b.asset_tag ?? "";
        break;
      case "type":
        aVal = a.product?.type ?? "";
        bVal = b.product?.type ?? "";
        break;
      case "price":
        aVal = parseFloat(a.product?.price || 0);
        bVal = parseFloat(b.product?.price || 0);
        break;
      default:
        aVal = "";
        bVal = "";
    }
    if (aVal < bVal) return sortConfig.direction === "asc" ? -1 : 1;
    if (aVal > bVal) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  const handleAddSingle = async (asset) => {
    try {
      const payload = {
        asset_tag: asset.code,
        name: asset.name,
        type: asset.type,
        price: asset.price,
        owner_user: asset.owner_user || null,
      };
      const res = await api.post("inventory/assets/", payload);
      setAssets((prev) => [...prev, res.data]);
      setEditingProduct(null);
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  const handleAddCSV = async (csvData) => {
    try {
      const formData = new FormData();
      const csvFile = new Blob(
        [
          [
            "名稱,財產編號,種類,價格,持有人",
            ...csvData.map((row) =>
              [row["名稱"], row["財產編號"], row["種類"], row["價格"], row["持有人"]].join(",")
            ),
          ].join("\n"),
        ],
        { type: "text/csv" }
      );
      formData.append("file", csvFile, "import.csv");

      const res = await api.post("inventory/assets/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setAssets((prev) => [...prev, ...res.data]);
    } catch (err) {
      console.error(err.response?.data || err);
      alert("CSV 匯入失敗，請查看 console");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("確定要刪除嗎？")) return;
    try {
      await api.delete(`inventory/assets/${id}/`);
      setAssets((prev) => prev.filter((p) => p.id !== id));
    } catch (err) {
      console.error(err);
      alert("刪除失敗");
    }
  };

  const handleEdit = (asset) => {
    const flattened = {
      name: asset.product?.name || "",
      code: asset.asset_tag || "",
      type: asset.product?.type || "",
      price: asset.product?.price || "",
      owner_user: asset.owner_user || "",
    };
    setEditingProduct(f);
    console.log(asset);
  };
  const cancelEdit = () => setEditingProduct(null);

  const toggleDetailedMode = () => {
    setDetailedMode((prev) => {
      localStorage.setItem("inventoryDetailedMode", !prev);
      return !prev;
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>
      <button onClick={toggleDetailedMode} style={{ marginBottom: "10px" }}>
        {detailedMode ? "切換為簡略模式" : "切換為詳細模式"}
      </button>

      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingProduct}
        onOwnerInput={handleOwnerInput}
      />

      {editingProduct && (
        <button
          onClick={cancelEdit}
          style={{ marginBottom: "10px", backgroundColor: "#ccc" }}
        >
          取消編輯
        </button>
      )}

      <h2>產品列表</h2>
      {sortedAssets.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th onClick={() => handleSort("name")} style={{ cursor: "pointer" }}>
                名稱 {sortConfig.key === "name" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th onClick={() => handleSort("code")} style={{ cursor: "pointer" }}>
                財產編號 {sortConfig.key === "code" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th onClick={() => handleSort("type")} style={{ cursor: "pointer" }}>
                種類 {sortConfig.key === "type" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th onClick={() => handleSort("price")} style={{ cursor: "pointer" }}>
                價格 {sortConfig.key === "price" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th>持有人</th>
              {detailedMode && <th>借用時間</th>}
              {detailedMode && <th>歸還時間</th>}
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {sortedAssets.map((p, idx) => (
              <tr key={p.id}>
                <td>{idx + 1}</td>
                <td>{p.product?.name ?? ""}</td>
                <td>{p.asset_tag}</td>
                <td>{p.product?.type ?? ""}</td>
                <td>{p.product?.price ?? ""}</td>
                <td>{p.owner_user?.name ?? "無"}</td>
                {detailedMode && <td>{p.borrowed_at ?? "-"}</td>}
                {detailedMode && <td>{p.returned_at ?? "-"}</td>}
                <td>
                  <button onClick={() => handleEdit(p)} style={{ marginRight: "5px" }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(p.id)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Inventory;
