import React, { useState, useEffect } from "react";
import api from "../api";

export default function ProductList({ products, onEdit, refresh, onViewHistory }) {
  const [users, setUsers] = useState([]);
  const [userAssets, setUserAssets] = useState({}); // { productId: [{user, quantity}] }

  useEffect(() => {
    // 取得使用者列表
    const fetchUsers = async () => {
      try {
        const res = await api.get("users/");
        setUsers(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchUsers();

    // 取得每個產品在使用者手上的資料
    const fetchUserAssets = async () => {
      try {
        const res = await api.get("inventory/user-assets/"); // 後端需提供對應 API
        // res.data = { productId: [{id, name, quantity}] }
        setUserAssets(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchUserAssets();
  }, [refresh]);

  const handleTransaction = async (product, type) => {
    let qty = parseInt(prompt("輸入數量："), 10);
    if (!qty || qty <= 0) return;

    let userId = null;
    if (type === "OUT") {
      const userName = prompt(
        `輸出給哪位使用者？\n可用使用者: ${users.map(u => u.name).join(", ")}`
      );
      const user = users.find(u => u.name === userName);
      if (!user) return alert("使用者不存在");
      userId = user.id;
    }

    try {
      await api.post(`inventory/products/${product.id}/transaction/`, {
        transaction_type: type,
        quantity: qty,
        user_id: userId,
      });
      refresh();
    } catch (err) {
      alert(err.response?.data?.error || "操作失敗");
    }
  };

  return (
    <table border="1" cellPadding="5">
      <thead>
        <tr>
          <th>名稱</th>
          <th>代碼</th>
          <th>單位</th>
          <th>價格</th>
          <th>倉庫庫存</th>
          <th>使用者持有</th>
          <th>操作</th>
        </tr>
      </thead>
      <tbody>
        {products.map((p) => (
          <tr key={p.id}>
            <td>{p.name}</td>
            <td>{p.code}</td>
            <td>{p.unit}</td>
            <td>{p.price}</td>
            <td>{p.quantity}</td>
            <td>
              {userAssets[p.id] ? (
                userAssets[p.id].map(u => `${u.name}: ${u.quantity}`).join(", ")
              ) : (
                "無"
              )}
            </td>
            <td>
              <button onClick={() => onEdit(p)}>編輯</button>{" "}
              <button onClick={() => handleTransaction(p, "IN")}>入庫</button>{" "}
              <button onClick={() => handleTransaction(p, "OUT")}>出庫</button>{" "}
              <button onClick={() => onViewHistory(p.id)}>庫存歷史</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
