import React, { useState, useEffect } from "react";

export default function AssetForm({ asset, onSave, onCancel, onOpenPeopleModal }) {
  const [formData, setFormData] = useState({
    name: "",
    asset_tag: "",
    type: "",
    price: "",
    owner_user: null,
  });

  useEffect(() => {
    if (asset) {
      setFormData({
        name: asset.product.name,
        asset_tag: asset.asset_tag,
        type: asset.product.type,
        price: asset.product.price,
        owner_user: asset.owner_user || null,
      });
    }
  }, [asset]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    // 持有人欄位單獨處理
    if (name === "owner_user_name") {
      setFormData((prev) => ({
        ...prev,
        owner_user_name: value,
        owner_user: null, // 清空已選擇
      }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleOwnerBlur = () => {
    if (formData.owner_user_name?.trim()) {
      onOpenPeopleModal("owner_user", formData.owner_user_name.trim());
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.asset_tag || !formData.name) {
      alert("名稱與財產編號為必填");
      return;
    }

    const payload = {
      name: formData.name,
      asset_tag: formData.asset_tag,
      type: formData.type,
      price: parseFloat(formData.price || 0),
      owner_user: formData.owner_user?.id || null,
    };

    onSave(payload);
    setFormData({
      name: "",
      asset_tag: "",
      type: "",
      price: "",
      owner_user: null,
    });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
      <div>
        <label>名稱: </label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>財產編號: </label>
        <input
          type="text"
          name="asset_tag"
          value={formData.asset_tag}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>種類: </label>
        <input type="text" name="type" value={formData.type} onChange={handleChange} />
      </div>
      <div>
        <label>價格: </label>
        <input type="number" name="price" value={formData.price} onChange={handleChange} />
      </div>
      <div>
        <label>持有人: </label>
        <input
          type="text"
          name="owner_user_name"
          value={formData.owner_user?.name || formData.owner_user_name || ""}
          onChange={handleChange}
          onBlur={handleOwnerBlur}
        />
      </div>
      <div style={{ marginTop: "10px" }}>
        <button type="submit" style={{ marginRight: "10px" }}>
          {asset ? "更新" : "新增"}
        </button>
        {asset && (
          <button type="button" onClick={onCancel}>
            取消
          </button>
        )}
      </div>
    </form>
  );
}
