import React, { useState, useEffect } from "react";
import api from "../api";

export default function ProductList({ products, onEdit, refresh, onViewHistory }) {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // 取得使用者列表供出庫選擇
    const fetchUsers = async () => {
      try {
        const res = await api.get("users/");
        setUsers(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchUsers();
  }, []);

  const handleTransaction = async (product, type) => {
    let qty = parseInt(prompt("輸入數量："), 10);
    if (!qty || qty <= 0) return;

    let userId = null;
    if (type === "OUT") {
      const userName = prompt(
        `輸出給哪位使用者？\n可用使用者: ${users.map(u => u.name).join(", ")}`
      );
      const user = users.find(u => u.name === userName);
      if (!user) return alert("使用者不存在");
      userId = user.id;
    }

    try {
      await api.post(`inventory/products/${product.id}/transaction/`, {
        transaction_type: type,
        quantity: qty,
        user_id: userId,
      });
      refresh();
    } catch (err) {
      alert(err.response?.data?.error || "操作失敗");
    }
  };

  return (
    <table border="1" cellPadding="5">
      <thead>
        <tr>
          <th>名稱</th>
          <th>代碼</th>
          <th>單位</th>
          <th>價格</th>
          <th>庫存</th>
          <th>持有人</th>
          <th>操作</th>
        </tr>
      </thead>
      <tbody>
        {products.map((p) => (
          <tr key={p.id}>
            <td>{p.name}</td>
            <td>{p.code}</td>
            <td>{p.unit}</td>
            <td>{p.price}</td>
            <td>{p.quantity}</td>
            <td>{p.owner_user?.name || "未分配"}</td>
            <td>
              <button onClick={() => onEdit(p)}>編輯</button>{" "}
              <button onClick={() => handleTransaction(p, "IN")}>入庫</button>{" "}
              <button onClick={() => handleTransaction(p, "OUT")}>出庫</button>{" "}
              <button onClick={() => onViewHistory(p.id)}>庫存歷史</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
