import React, { useState } from "react";
import GenericForm from "../components/GenericForm";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);

  const fields = [
    { name: "name", label: "姓名", type: "text" },
    { name: "email", label: "Email", type: "email" },
    { name: "role", label: "職稱", type: "text" },
  ];

  const handleAddSingle = (person) => setPeople([...people, person]);

  const handleAddCSV = (csvData) =>
    setPeople([
      ...people,
      ...csvData.map((r) => ({
        name: r.name,
        email: r.email,
        role: r.role,
      })),
    ]);

  const handleDelete = (index) => setPeople(people.filter((_, idx) => idx !== index));

  return (
    <div style={{ padding: "20px" }}>
      <h1>人員管理</h1>
      <GenericForm fields={fields} onAddSingle={handleAddSingle} onAddCSV={handleAddCSV} />

      <h2>人員列表</h2>
      {people.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th>姓名</th>
              <th>Email</th>
              <th>職稱</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {people.map((p, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.email}</td>
                <td>{p.role}</td>
                <td>
                  <button onClick={() => handleDelete(idx)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PeopleManagement;
