import React, { useState } from "react";
import api from "../api";

export default function ImportCSV({ onSuccess }) {
  const [file, setFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return alert("請選擇 CSV 檔案");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await api.post("inventory/products/import_csv/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert(`匯入成功，新增 ${res.data.created} 筆資料`);
      onSuccess();
    } catch (err) {
      alert(err.response?.data?.error || "匯入失敗");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="file" accept=".csv" onChange={(e) => setFile(e.target.files[0])} />
      <button type="submit">匯入 CSV</button>
    </form>
  );
}
