import React, { useEffect, useState } from "react";
import axios from "axios";

const PeopleManagement = ({ idNumber, onSaved }) => {
  const [user, setUser] = useState({
    id_number: "",
    eip_account: "",
    name: "",
    department_name: "",
    email: "",
    phone: "",
    title: ""
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // 取得單一使用者資料
  const fetchUser = async () => {
    try {
      const res = await axios.get(`http://127.0.0.1:8000/api/users/${idNumber}/`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("access_token")}` }
      });
      setUser(res.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError("無法取得使用者資料");
      setLoading(false);
    }
  };

  useEffect(() => { fetchUser(); }, [idNumber]);

  const handleChange = (e) => {
    setUser(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await axios.put(`http://127.0.0.1:8000/api/users/${idNumber}/`, user, {
        headers: { Authorization: `Bearer ${localStorage.getItem("access_token")}` }
      });
      alert("更新成功");
      if (onSaved) onSaved(); // 可回傳給父層更新列表
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.detail || "更新失敗");
    }
  };

  if (loading) return <p>載入中...</p>;

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>編輯人員資料</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>EIP帳號：</label>
          <input
            type="text"
            name="eip_account"
            value={user.eip_account || ""}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>姓名：</label>
          <input
            type="text"
            name="name"
            value={user.name || ""}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>部門：</label>
          <input
            type="text"
            name="department_name"
            value={user.department_name || ""}
            readOnly
          />
        </div>
        <div>
          <label>Email：</label>
          <input
            type="email"
            name="email"
            value={user.email || ""}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>電話：</label>
          <input
            type="text"
            name="phone"
            value={user.phone || ""}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>職稱：</label>
          <input
            type="text"
            name="title"
            value={user.title || ""}
            onChange={handleChange}
          />
        </div>
        <button type="submit" style={{ marginTop: "15px" }}>儲存變更</button>
      </form>
    </div>
  );
};

export default PeopleManagement;
