import React, { useState, useEffect } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [editingPerson, setEditingPerson] = useState(null);

  // 取得現有人員
  useEffect(() => {
    const fetchPeople = async () => {
      try {
        const res = await api.get("people/");
        setPeople(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPeople();
  }, []);

  const fields = [
    { name: "name", label: "姓名", type: "text" },
    { name: "id_number", label: "身份證號", type: "text" },
    { name: "email", label: "信箱", type: "email" },
    { name: "phone", label: "電話", type: "text" },
    { name: "department", label: "部門", type: "text" },
  ];

  // 單筆新增或更新
  const handleAddSingle = async (person) => {
    try {
      const payload = {
        name: person.name ?? "",
        id_number: person.id_number ?? "",
        email: person.email ?? "",
        phone: person.phone ?? "",
        department: person.department ?? "",
      };
      console.log("單筆送出資料:", payload);

      // 判斷是更新還是新增
      const existing = people.find((p) => p.id_number === payload.id_number);
      if (existing) {
        // 更新
        const res = await api.put(`people/${payload.id_number}/`, payload);
        console.log("更新回應:", res.data);
        setPeople((prev) =>
          prev.map((p) =>
            p.id_number === payload.id_number ? res.data : p
          )
        );
      } else {
        // 新增
        const res = await api.post("people/", payload);
        console.log("新增回應:", res.data);
        setPeople((prev) => [...prev, res.data]);
      }
      setEditingPerson(null);
    } catch (err) {
      console.error("單筆新增/更新失敗:", err.response?.data || err);
      alert("單筆新增/更新失敗，請查看 console");
    }
  };

  // CSV 匯入
  const handleAddCSV = async (csvData) => {
    try {
      const created = [];
      for (let row of csvData) {
        const payload = {
  name: row["姓名"]?.trim() ?? "",
  id_number: row["身份證字號"]?.trim() ?? "",
  email: row["信箱"]?.trim() ?? "",
  phone: row["電話"]?.trim() ?? "",
  department: row["部門"]?.trim() ?? "",
};


        if (!payload.name || !payload.id_number) {
          console.warn("CSV 欄位不足，忽略此行:", row);
          continue;
        }

        // 判斷已存在就 PUT，否則 POST
        const existing = people.find((p) => p.id_number === payload.id_number);
        if (existing) {
          const res = await api.put(`people/${payload.id_number}/`, payload);
          console.log("CSV 更新回應:", res.data);
          setPeople((prev) =>
            prev.map((p) =>
              p.id_number === payload.id_number ? res.data : p
            )
          );
        } else {
          const res = await api.post("people/", payload);
          console.log("CSV 新增回應:", res.data);
          created.push(res.data);
        }
      }
      if (created.length > 0) {
        setPeople((prev) => [...prev, ...created]);
      }
    } catch (err) {
      console.error("CSV 匯入失敗:", err.response?.data || err);
      alert("CSV 匯入失敗，請查看 console");
    }
  };

  const handleDelete = async (id_number) => {
    if (!window.confirm("確定刪除此人員？")) return;
    try {
      await api.delete(`people/${id_number}/`);
      setPeople((prev) => prev.filter((p) => p.id_number !== id_number));
    } catch (err) {
      console.error("刪除失敗:", err.response?.data || err);
      alert("刪除失敗，請查看 console");
    }
  };

  const handleEdit = (person) => setEditingPerson(person);

  return (
    <div style={{ padding: "20px" }}>
      <h1>人員管理</h1>
      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingPerson || {}}
      />

      <h2>人員列表</h2>
      {people.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th>姓名</th>
              <th>身份證號</th>
              <th>信箱</th>
              <th>電話</th>
              <th>部門</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {people.map((p, idx) => (
              <tr key={p.id_number}>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.id_number}</td>
                <td>{p.email}</td>
                <td>{p.phone}</td>
                <td>{p.department}</td>
                <td>
                  <button onClick={() => handleEdit(p)}>編輯</button>{" "}
                  <button onClick={() => handleDelete(p.id_number)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PeopleManagement;
