import React from 'react';

export default function DepartmentsList({ departments = [], onEdit, onDelete }) {
  if (!departments.length) {
    return <p>尚無部門資料</p>;
  }

  return (
    <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
      <thead>
        <tr>
          <th>ID</th>
          <th>名稱</th>
          <th>操作</th>
        </tr>
      </thead>
      <tbody>
        {departments.map((dept) => (
          <tr key={dept.id}>
            <td>{dept.id}</td>
            <td>{dept.name}</td>
            <td>
              <button onClick={() => onEdit(dept)}>編輯</button>
              <button onClick={() => onDelete(dept.id)}>刪除</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
