import React, { useState, useEffect } from "react";

// È†êË®≠Ê¨Ñ‰ΩçÔºàÁ∞°Êòì + Ë©≥Á¥∞Ôºâ
const simpleColumns = ["ÂêçÁ®±", "Êï∏Èáè", "ÊåÅÊúâ‰∫∫"];
const detailedColumns = [
  "ÂêçÁ®±",
  "Ë≤°Áî¢Á∑®Ëôü",
  "Á®ÆÈ°û",
  "ÂÉπÊ†º",
  "Êï∏Èáè",
  "ÊåÅÊúâ‰∫∫",
  "ÂÄüÁî®ÊôÇÈñì",
  "Ê≠∏ÈÇÑÊôÇÈñì",
  "ÁãÄÊÖã",
  "‰ΩçÁΩÆ",
  "ÂÇôË®ª"
];

export default function InventoryTable({ data }) {
  const [detailedMode, setDetailedMode] = useState(false);

  // ÂàùÂßãÂåñÔºöÂæû localStorage ËÆÄÂèñË®≠ÂÆö
  useEffect(() => {
    const savedPref = localStorage.getItem("inventoryMode");
    if (savedPref === "detailed") {
      setDetailedMode(true);
    }
  }, []);

  // ÂàáÊèõÊ®°Âºè‰∏¶Â≠òÂÅèÂ•Ω
  const toggleMode = () => {
    setDetailedMode((prev) => {
      const newMode = !prev;
      localStorage.setItem("inventoryMode", newMode ? "detailed" : "simple");
      return newMode;
    });
  };

  const columns = detailedMode ? detailedColumns : simpleColumns;

  return (
    <div>
      <div className="mb-2 flex justify-between items-center">
        <h2 className="text-lg font-bold">üì¶ Â∫´Â≠òÁÆ°ÁêÜ</h2>
        <button
          onClick={toggleMode}
          className="px-3 py-1 rounded bg-blue-500 text-white hover:bg-blue-600"
        >
          {detailedMode ? "ÂàáÊèõÂà∞Á∞°ÊòìÊ®°Âºè" : "ÂàáÊèõÂà∞Ë©≥Á¥∞Ê®°Âºè"}
        </button>
      </div>

      <table className="w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            {columns.map((col) => (
              <th key={col} className="border px-2 py-1">
                {col}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((item, idx) => (
            <tr key={idx} className="hover:bg-gray-50">
              {columns.map((col) => (
                <td key={col} className="border px-2 py-1 text-center">
                  {item[col] || "-"}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
