import React, { useState } from "react";
import Papa from "papaparse";

const GenericForm = ({ fields, onAddSingle, onAddCSV }) => {
  const [mode, setMode] = useState("single");
  const initialState = fields.reduce((acc, f) => ({ ...acc, [f.name]: "" }), {});
  const [formData, setFormData] = useState(initialState);
  const [csvFile, setCsvFile] = useState(null);

  const handleModeChange = (newMode) => {
    setMode(newMode);
    setFormData(initialState);
    setCsvFile(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSingleSubmit = (e) => {
    e.preventDefault();
    onAddSingle(formData);
    setFormData(initialState);
  };

  const handleCSVChange = (e) => setCsvFile(e.target.files[0]);

  const handleCSVSubmit = (e) => {
    e.preventDefault();
    if (!csvFile) return;
    Papa.parse(csvFile, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        onAddCSV(results.data);
        setCsvFile(null);
      },
    });
  };

  return (
    <div style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "20px" }}>
      <div style={{ marginBottom: "10px" }}>
        <button type="button" onClick={() => handleModeChange("single")} style={{ marginRight: "10px", background: mode === "single" ? "#007bff" : "#ccc", color: "#fff" }}>
          單筆新增
        </button>
        <button type="button" onClick={() => handleModeChange("csv")} style={{ background: mode === "csv" ? "#007bff" : "#ccc", color: "#fff" }}>
          CSV 匯入
        </button>
      </div>

      {mode === "single" && (
        <form onSubmit={handleSingleSubmit}>
          {fields.map((f) => (
            <input
              key={f.name}
              type={f.type}
              name={f.name}
              placeholder={f.label}
              value={formData[f.name]}
              onChange={handleInputChange}
              required
              style={{ marginRight: "10px" }}
            />
          ))}
          <button type="submit">新增</button>
        </form>
      )}

      {mode === "csv" && (
        <form onSubmit={handleCSVSubmit}>
          <input type="file" accept=".csv" onChange={handleCSVChange} style={{ marginRight: "10px" }} />
          <button type="submit">匯入 CSV</button>
        </form>
      )}
    </div>
  );
};

export default GenericForm;
