import React, { useState } from "react";
import GenericForm from "../comp/GenericForm";

const Inventory = () => {
  const [products, setProducts] = useState([]);
  const fields = [
    { name: "name", label: "名稱", type: "text" },
    { name: "price", label: "價格", type: "number" },
    { name: "quantity", label: "數量", type: "number" },
  ];

  const handleAddSingle = (product) => setProducts([...products, product]);
  const handleAddCSV = (csvData) => setProducts([...products, ...csvData.map(r => ({ name: r.name, price: Number(r.price), quantity: Number(r.quantity) }))]);

  const handleDelete = (index) => setProducts(products.filter((_, idx) => idx !== index));

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>
      <GenericForm fields={fields} onAddSingle={handleAddSingle} onAddCSV={handleAddCSV} />

      <h2>產品列表</h2>
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr><th>#</th><th>名稱</th><th>價格</th><th>數量</th><th>操作</th></tr>
        </thead>
        <tbody>
          {products.map((p, idx) => (
            <tr key={idx}>
              <td>{idx + 1}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>{p.quantity}</td>
              <td><button onClick={() => handleDelete(idx)}>刪除</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Inventory;
