import React, { useState, useEffect } from "react";
import api from "../api";

export default function ProductForm({ product, onClose }) {
  const [name, setName] = useState("");
  const [code, setCode] = useState("");
  const [unit, setUnit] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);

  useEffect(() => {
    if (product) {
      setName(product.name);
      setCode(product.code);
      setUnit(product.unit);
      setPrice(product.price);
      setQuantity(product.quantity);
    } else {
      setName("");
      setCode("");
      setUnit("");
      setPrice(0);
      setQuantity(0);
    }
  }, [product]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = product
      ? `inventory/products/${product.id}/`
      : "inventory/products/";
    const method = product ? "put" : "post";

    try {
      await api[method](url, { name, code, unit, price, quantity });
      onClose();
    } catch (err) {
      alert(err.response?.data?.error || "操作失敗");
    }
  };

  if (!product && name === "" && code === "") return null;

  return (
    <div>
      <h2>{product ? "編輯商品" : "新增商品"}</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="名稱" value={name} onChange={(e) => setName(e.target.value)} required />
        <input placeholder="代碼" value={code} onChange={(e) => setCode(e.target.value)} required />
        <input placeholder="單位" value={unit} onChange={(e) => setUnit(e.target.value)} />
        <input type="number" placeholder="價格" value={price} onChange={(e) => setPrice(e.target.value)} />
        <input type="number" placeholder="庫存" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
        <button type="submit">送出</button>
        <button type="button" onClick={onClose}>取消</button>
      </form>
    </div>
  );
}
