import React, { useEffect, useState } from "react";
import GenericForm from "../components/GenericForm";
import PeopleSelectModal from "../components/PeopleSelectModal";
import api from "../api";

const Inventory = () => {
  const [assets, setAssets] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });
  const [detailedMode, setDetailedMode] = useState(
    localStorage.getItem("inventoryDetailedMode") === "true"
  );

  const [peopleCandidates, setPeopleCandidates] = useState([]);
  const [showPeopleModal, setShowPeopleModal] = useState(false);
  const [selectedOwnerCallback, setSelectedOwnerCallback] = useState(null);

  const fields = [
    { name: "name", label: "名稱", type: "text" },
    { name: "code", label: "財產編號", type: "text" },
    { name: "type", label: "種類", type: "text" },
    { name: "price", label: "價格", type: "number" },
    { name: "owner_user", label: "持有人", type: "text" },
  ];

  // 取得資產列表
  useEffect(() => {
    const fetchAssets = async () => {
      try {
        const res = await api.get("inventory/assets/");
        setAssets(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAssets();
  }, []);

  // 持有人搜尋
  const handleOwnerInput = async (name, callback) => {
    if (!name) return;
    try {
      const res = await api.get(`people/search/?name=${encodeURIComponent(name)}`);
      if (res.data.length === 1) {
        callback(res.data[0].id);
      } else if (res.data.length > 1) {
        setPeopleCandidates(res.data);
        setSelectedOwnerCallback(() => callback);
        setShowPeopleModal(true);
      } else {
        alert("找不到符合的員工");
      }
    } catch (err) {
      console.error(err);
      alert("搜尋員工失敗");
    }
  };

  // 排序
  const handleSort = (key) => {
    setSortConfig((prev) => {
      const direction = prev.key === key && prev.direction === "asc" ? "desc" : "asc";
      return { key, direction };
    });
  };

  const sortedAssets = [...assets].sort((a, b) => {
    let aVal, bVal;
    switch (sortConfig.key) {
      case "name":
        aVal = a.product.name;
        bVal = b.product.name;
        break;
      case "code":
        aVal = a.asset_tag;
        bVal = b.asset_tag;
        break;
      case "type":
        aVal = a.product.type;
        bVal = b.product.type;
        break;
      case "price":
        aVal = parseFloat(a.product.price);
        bVal = parseFloat(b.product.price);
        break;
      default:
        aVal = "";
        bVal = "";
    }
    if (aVal < bVal) return sortConfig.direction === "asc" ? -1 : 1;
    if (aVal > bVal) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  // 單筆新增或 CSV 單筆處理
  const handleAssetCreate = async (asset) => {
    try {
      const payload = {
        asset_tag: asset.code,
        name: asset.name,
        type: asset.type,
        price: asset.price,
        owner_user: asset.owner_user || null,
      };
      const res = await api.post("inventory/assets/", payload);
      if (res.status === 201 || res.status === 200) {
        setAssets((prev) => [...prev, res.data]);
        setEditingProduct(null);
      } else if (res.status === 409 && res.data.candidates) {
        setPeopleCandidates(res.data.candidates);
        setSelectedOwnerCallback(() => (id) => handleAssetCreate({ ...asset, owner_user: id }));
        setShowPeopleModal(true);
      }
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  // 單筆新增
  const handleAddSingle = handleAssetCreate;

  // CSV 匯入
  const handleAddCSV = async (csvData) => {
    for (const row of csvData) {
      const asset = {
        name: row["名稱"],
        code: row["財產編號"],
        type: row["種類"] || "",
        price: row["價格"] ? parseFloat(row["價格"]) : 0,
        owner_user: null,
      };

      if (row["持有人"]) {
        await handleOwnerInput(row["持有人"], (id) => {
          asset.owner_user = id;
        });
        // 如果選擇 Modal，handleAssetCreate 會在選擇後自動呼叫
        if (showPeopleModal) return; // 停止循環等待使用者選擇
      }

      await handleAssetCreate(asset);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("確定要刪除嗎？")) return;
    try {
      await api.delete(`inventory/assets/${id}/`);
      setAssets((prev) => prev.filter((p) => p.id !== id));
    } catch (err) {
      console.error(err);
      alert("刪除失敗");
    }
  };

  const handleEdit = (asset) => setEditingProduct(asset);
  const cancelEdit = () => setEditingProduct(null);

  const toggleDetailedMode = () => {
    setDetailedMode((prev) => {
      localStorage.setItem("inventoryDetailedMode", !prev);
      return !prev;
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>
      <button onClick={toggleDetailedMode} style={{ marginBottom: "10px" }}>
        {detailedMode ? "切換為簡略模式" : "切換為詳細模式"}
      </button>

      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingProduct}
        onOwnerInput={handleOwnerInput}
      />

      {editingProduct && (
        <button
          onClick={cancelEdit}
          style={{ marginBottom: "10px", backgroundColor: "#ccc" }}
        >
          取消編輯
        </button>
      )}

      {showPeopleModal && (
        <PeopleSelectModal
          candidates={peopleCandidates}
          onClose={() => setShowPeopleModal(false)}
          onSelect={(person) => {
            if (selectedOwnerCallback) selectedOwnerCallback(person.id);
            setShowPeopleModal(false);
          }}
        />
      )}

      <h2>產品列表</h2>
      {sortedAssets.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th onClick={() => handleSort("name")} style={{ cursor: "pointer" }}>
                名稱 {sortConfig.key === "name" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th onClick={() => handleSort("code")} style={{ cursor: "pointer" }}>
                財產編號 {sortConfig.key === "code" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th onClick={() => handleSort("type")} style={{ cursor: "pointer" }}>
                種類 {sortConfig.key === "type" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th onClick={() => handleSort("price")} style={{ cursor: "pointer" }}>
                價格 {sortConfig.key === "price" ? (sortConfig.direction === "asc" ? "↑" : "↓") : ""}
              </th>
              <th>持有人</th>
              {detailedMode && <th>借用時間</th>}
              {detailedMode && <th>歸還時間</th>}
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {sortedAssets.map((p, idx) => (
              <tr key={p.id}>
                <td>{idx + 1}</td>
                <td>{p.product.name}</td>
                <td>{p.asset_tag}</td>
                <td>{p.product.type}</td>
                <td>{p.product.price}</td>
                <td>{p.holder?.name ?? "無"}</td>
                {detailedMode && <td>{p.borrowed_at ?? "-"}</td>}
                {detailedMode && <td>{p.returned_at ?? "-"}</td>}
                <td>
                  <button onClick={() => handleEdit(p)} style={{ marginRight: "5px" }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(p.id)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Inventory;
