import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import PeopleManagement from "./pages/PeopleManagement";
import AssetManagement from "./pages/AssetManagement";
import InventoryRecords from "./pages/InventoryRecords";
import Reports from "./pages/Reports";
import Settings from "./pages/Settings";
import Login from "./pages/Login";

const App = () => {
  const isAuthenticated = !!localStorage.getItem("access_token");

  return (
    <Router>
      <div style={{ padding: "20px" }}>
        <nav style={{ marginBottom: "20px" }}>
          <Link to="/">首頁</Link> |{" "}
          <Link to="/people">人員管理</Link> |{" "}
          <Link to="/assets">資產管理</Link> |{" "}
          <Link to="/inventory">出入庫紀錄</Link> |{" "}
          <Link to="/reports">報表</Link> |{" "}
          <Link to="/settings">系統設定</Link>
        </nav>

        <Routes>
          <Route path="/" element={<h1>資產管理系統首頁</h1>} />
          <Route
            path="/people"
            element={isAuthenticated ? <PeopleManagement /> : <Navigate to="/login" />}
          />
          <Route
            path="/assets"
            element={isAuthenticated ? <AssetManagement /> : <Navigate to="/login" />}
          />
          <Route
            path="/inventory"
            element={isAuthenticated ? <InventoryRecords /> : <Navigate to="/login" />}
          />
          <Route
            path="/reports"
            element={isAuthenticated ? <Reports /> : <Navigate to="/login" />}
          />
          <Route
            path="/settings"
            element={isAuthenticated ? <Settings /> : <Navigate to="/login" />}
          />
          <Route path="/login" element={<Login />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
