import React, { useEffect, useState } from "react";
import api from "../api";

const StockTransactionPage = () => {
  const [products, setProducts] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [transactionType, setTransactionType] = useState("IN");
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [selectedUserId, setSelectedUserId] = useState("");
  const [userSearch, setUserSearch] = useState("");
  const [remark, setRemark] = useState("");

  // 取得產品列表
  const fetchProducts = async () => {
    try {
      const res = await api.get("inventory/products/");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
      alert("載入產品失敗");
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // 取得部門列表
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await api.get("departments/");
        setDepartments(res.data);
      } catch (err) {
        console.error(err);
        alert("載入部門失敗");
      }
    };
    fetchDepartments();
  }, []);

  // 取得使用者列表（依部門）
  useEffect(() => {
    const fetchUsers = async () => {
      if (!selectedDepartment) {
        setUsers([]);
        setFilteredUsers([]);
        return;
      }
      try {
        const res = await api.get(`users/?department_id=${selectedDepartment}`);
        setUsers(res.data);
        setFilteredUsers(res.data);
      } catch (err) {
        console.error(err);
        alert("載入使用者失敗");
      }
    };
    fetchUsers();
  }, [selectedDepartment]);

  // 即時搜尋使用者
  useEffect(() => {
    const filtered = users.filter(
      (u) =>
        u.name.toLowerCase().includes(userSearch.toLowerCase()) ||
        u.email.toLowerCase().includes(userSearch.toLowerCase())
    );
    setFilteredUsers(filtered);
  }, [userSearch, users]);

  const handleSubmit = async () => {
    if (!selectedProduct) {
      alert("請選擇產品");
      return;
    }
    if (transactionType === "OUT" && !selectedUserId) {
      alert("出庫需選擇使用者");
      return;
    }

    try {
      const payload = {
        asset_tag: selectedProduct.code,
        transaction_type: transactionType,
        user_id: transactionType === "OUT" ? selectedUserId : null,
        remark,
      };
      await api.post("inventory/stock_transaction/", payload);
      alert("操作成功");

      // 重置表單
      setSelectedProduct(null);
      setTransactionType("IN");
      setSelectedDepartment("");
      setSelectedUserId("");
      setUserSearch("");
      setRemark("");

      // 自動刷新產品列表
      fetchProducts();
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>商品出入庫</h1>

      <div style={{ marginBottom: "10px" }}>
        <label>選擇產品：</label>
        <select
          value={selectedProduct?.id || ""}
          onChange={(e) =>
            setSelectedProduct(
              products.find((p) => p.id === parseInt(e.target.value))
            )
          }
        >
        <option value="">請選擇</option>
        {products.map((p) => (
            <option
                key={p.id}
                value={p.id}
                disabled={!p.user_assets || p.user_assets.length === 0} // 沒資產就不能選
            >
                {p.name} ({p.code}) {p.user_assets?.length > 0 ? "" : "(無資產)"}
            </option>
        ))}
        </select>
      </div>

      <div style={{ marginBottom: "10px" }}>
        <label>交易類型：</label>
        <select
          value={transactionType}
          onChange={(e) => setTransactionType(e.target.value)}
        >
          <option value="IN">入庫</option>
          <option value="OUT">出庫</option>
        </select>
      </div>

      {transactionType === "OUT" && (
        <>
          <div style={{ marginBottom: "10px" }}>
            <label>選擇部門：</label>
            <select
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
            >
              <option value="">-- 請選擇部門 --</option>
              {departments.map((d) => (
                <option key={d.id} value={d.id}>
                  {d.name}
                </option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>搜尋使用者：</label>
            <input
              type="text"
              placeholder="輸入姓名或Email"
              value={userSearch}
              onChange={(e) => setUserSearch(e.target.value)}
            />
          </div>

          <div style={{ marginBottom: "10px" }}>
            <label>選擇使用者：</label>
            <select
              value={selectedUserId}
              onChange={(e) => setSelectedUserId(e.target.value)}
            >
              <option value="">-- 請選擇使用者 --</option>
              {filteredUsers.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.name} ({u.email})
                </option>
              ))}
            </select>
          </div>
        </>
      )}

      <div style={{ marginBottom: "10px" }}>
        <label>備註：</label>
        <input
          type="text"
          value={remark}
          onChange={(e) => setRemark(e.target.value)}
        />
      </div>

      <button onClick={handleSubmit}>提交</button>

      <h2 style={{ marginTop: "20px" }}>產品列表（自動刷新）</h2>
      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>#</th>
            <th>名稱</th>
            <th>財產編號</th>
            <th>種類</th>
            <th>價格</th>
            <th>持有人</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p, idx) => (
            <tr key={p.id}>
              <td>{idx + 1}</td>
              <td>{p.name}</td>
              <td>{p.code}</td>
              <td>{p.type}</td>
              <td>{p.price}</td>
              <td>{p.user_assets.length > 0 ? p.user_assets[0].name : "無"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTransactionPage;
