import React, { useEffect, useState } from "react";
import api from "../api"; // 自訂 axios 實例
import GenericForm from "../components/GenericForm";

const PeopleManagement = ({ idNumber }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchUser = async () => {
    if (!idNumber) return; // 防止 undefined
    setLoading(true);
    try {
      const res = await api.get(`/users/${idNumber}/`);
      setUser(res.data);
    } catch (err) {
      console.error(err);
      setError("無法取得使用者資料");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUser();
  }, [idNumber]);

  const handleUpdate = async (updatedData) => {
    if (!idNumber) return;
    try {
      const res = await api.put(`/users/${idNumber}/`, updatedData);
      setUser(res.data);
      alert("更新成功！");
    } catch (err) {
      console.error(err);
      alert("更新失敗！");
    }
  };

  if (!idNumber) return <p>請選擇一位使用者進行管理</p>;
  if (loading) return <p>資料載入中...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!user) return null;

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto" }}>
      <h2>使用者管理</h2>
      <GenericForm initialData={user} onSubmit={handleUpdate} />
    </div>
  );
};

export default PeopleManagement;
