import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "../api"; // 你的 axios 實例

const UserDetail = () => {
  const { id_number } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(`/users/${id_number}/`);
        setUser(res.data);
      } catch (err) {
        setError(err.response?.data?.detail || "載入失敗");
      } finally {
        setLoading(false);
      }
    };
    fetchUser();
  }, [id_number]);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`/users/${id_number}/`, user);
      alert("更新成功");
    } catch (err) {
      setError(err.response?.data?.detail || "更新失敗");
    }
  };

  const handleDelete = async () => {
    if (!window.confirm("確定要刪除此使用者嗎？")) return;
    try {
      await axios.delete(`/users/${id_number}/`);
      alert("刪除成功");
      navigate("/users");
    } catch (err) {
      setError(err.response?.data?.detail || "刪除失敗");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h2>使用者詳細資料</h2>
      <div>
        <label>姓名：</label>
        <input name="name" value={user.name} onChange={handleChange} />
      </div>
      <div>
        <label>Email：</label>
        <input name="email" value={user.email} onChange={handleChange} />
      </div>
      <div>
        <label>電話：</label>
        <input name="phone" value={user.phone} onChange={handleChange} />
      </div>
      <div>
        <label>部門：</label>
        <input name="department_name" value={user.department_name} readOnly />
      </div>
      <div style={{ marginTop: "10px" }}>
        <button onClick={handleUpdate}>更新</button>
        <button onClick={handleDelete} style={{ marginLeft: "10px", color: "red" }}>
          刪除
        </button>
      </div>
    </div>
  );
};

export default UserDetail;
