import React, { useState, useEffect } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const Inventory = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [sortConfig, setSortConfig] = useState({ key: "name", direction: "asc" });
  const [detailedMode, setDetailedMode] = useState(
    localStorage.getItem("inventoryDetailedMode") === "true"
  );

  const fields = [
    { name: "name", label: "名稱", type: "text" },
    { name: "code", label: "財產編號", type: "text" },
    { name: "type", label: "種類", type: "text" },
    { name: "price", label: "價格", type: "number" },
  ];

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await api.get("inventory/products/");
        setProducts(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProducts();
  }, []);

  const handleAddSingle = async (product) => {
    try {
      const payload = { ...product };
      if (editingProduct) {
        const res = await api.put(`inventory/products/${editingProduct.id}/`, payload);
        setProducts((prev) =>
          prev.map((p) => (p.id === editingProduct.id ? res.data : p))
        );
        setEditingProduct(null);
      } else {
        const res = await api.post("inventory/products/", payload);
        setProducts((prev) => [...prev, res.data]);
      }
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  const handleAddCSV = async (csvData) => {
    try {
      const created = [];
      for (let row of csvData) {
        const payload = {
          name: row["名稱"] ?? "",
          code: row["財產編號"] ?? "",
          type: row["種類"] ?? "",
          price: Number(row["價格"] ?? 0),
        };
        if (!payload.name || !payload.code) continue;
        const res = await api.post("inventory/assets/", payload);
        created.push(res.data);
      }
      setProducts((prev) => [...prev, ...created]);
    } catch (err) {
      console.error(err.response?.data || err);
      alert("CSV 匯入失敗，請查看 console");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("確定要刪除嗎？")) return;
    try {
      await api.delete(`inventory/assets/${id}/`);
      setProducts((prev) => prev.filter((p) => p.id !== id));
    } catch (err) {
      console.error(err);
      alert("刪除失敗");
    }
  };

  const handleEdit = (product) => setEditingProduct(product);
  const cancelEdit = () => setEditingProduct(null);

  const handleSort = (key) => {
    setSortConfig((prev) => ({
      key,
      direction: prev.key === key && prev.direction === "asc" ? "desc" : "asc",
    }));
  };

  const getSortIndicator = (key) => {
    if (sortConfig.key !== key) return null;
    return sortConfig.direction === "asc" ? " ↑" : " ↓";
  };

  const sortedProducts = [...products].sort((a, b) => {
    const key = sortConfig.key;
    const valA = a[key] ?? "";
    const valB = b[key] ?? "";
    if (valA < valB) return sortConfig.direction === "asc" ? -1 : 1;
    if (valA > valB) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  const toggleDetailedMode = () => {
    setDetailedMode((prev) => {
      localStorage.setItem("inventoryDetailedMode", !prev);
      return !prev;
    });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>
      <button onClick={toggleDetailedMode} style={{ marginBottom: "10px" }}>
        {detailedMode ? "切換為簡略模式" : "切換為詳細模式"}
      </button>

      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingProduct}
      />

      {editingProduct && (
        <button
          onClick={cancelEdit}
          style={{ marginBottom: "10px", backgroundColor: "#ccc" }}
        >
          取消編輯
        </button>
      )}

      <h2>產品列表</h2>
      {products.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th onClick={() => handleSort("name")} style={{ cursor: "pointer" }}>
                名稱{getSortIndicator("name")}
              </th>
              <th onClick={() => handleSort("code")} style={{ cursor: "pointer" }}>
                財產編號{getSortIndicator("code")}
              </th>
              <th onClick={() => handleSort("type")} style={{ cursor: "pointer" }}>
                種類{getSortIndicator("type")}
              </th>
              <th onClick={() => handleSort("price")} style={{ cursor: "pointer" }}>
                價格{getSortIndicator("price")}
              </th>
              <th onClick={() => handleSort("holder")} style={{ cursor: "pointer" }}>
                持有人{getSortIndicator("holder")}
              </th>
              {detailedMode && <th>借用時間</th>}
              {detailedMode && <th>歸還時間</th>}
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {sortedProducts.map((p, idx) => (
              <tr key={p.id}>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.code}</td>
                <td>{p.type}</td>
                <td>{p.price}</td>
                <td>{p.holder ?? "無"}</td>
                {detailedMode && <td>{p.borrowed_at ?? "-"}</td>}
                {detailedMode && <td>{p.returned_at ?? "-"}</td>}
                <td>
                  <button onClick={() => handleEdit(p)} style={{ marginRight: "5px" }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(p.id)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Inventory;
