import React, { useState, useEffect } from "react";
import ProductList from "../components/ProductList";
import ProductForm from "../components/ProductForm";
import StockHistory from "../components/StockHistory";
import ImportProductCSV from "../components/ImportProductCSV";
import Modal from "../components/Modal";
import api from "../api";

export default function Inventory() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [historyProductId, setHistoryProductId] = useState(null);
  const [showFormModal, setShowFormModal] = useState(false);

  const fetchProducts = async () => {
    try {
      const res = await api.get("inventory/products/");
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddClick = () => {
    setEditingProduct(null);
    setShowFormModal(true);
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setShowFormModal(true);
  };

  const handleFormClose = () => {
    setShowFormModal(false);
    setEditingProduct(null);
    fetchProducts();
  };

  const handleViewHistory = (productId) => setHistoryProductId(productId);
  const handleCloseHistory = () => setHistoryProductId(null);

  return (
    <div>
      <h1>商品庫存管理</h1>
      <ImportProductCSV onSuccess={fetchProducts} />
      <button onClick={handleAddClick}>新增商品</button>

      {showFormModal && (
        <Modal onClose={handleFormClose}>
          <ProductForm product={editingProduct} onClose={handleFormClose} />
        </Modal>
      )}

      <ProductList
        products={products}
        onEdit={handleEdit}
        refresh={fetchProducts}
        onViewHistory={handleViewHistory}
      />

      {historyProductId && (
        <StockHistory productId={historyProductId} onClose={handleCloseHistory} />
      )}
    </div>
  );
}
