import React, { useState } from "react";
import ProductForm from "../components/ProductForm";

const Inventory = () => {
  const [products, setProducts] = useState([]);

  // 單筆新增
  const handleAddSingle = (product) => {
    setProducts([...products, product]);
  };

  // CSV 匯入
  const handleAddCSV = (csvData) => {
    const formatted = csvData.map((row) => ({
      name: row.name,
      price: Number(row.price),
      quantity: Number(row.quantity),
    }));
    setProducts([...products, ...formatted]);
  };

  // 刪除單筆
  const handleDelete = (index) => {
    const newProducts = products.filter((_, idx) => idx !== index);
    setProducts(newProducts);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>
      <ProductForm onAddSingle={handleAddSingle} onAddCSV={handleAddCSV} />

      <h2>產品列表</h2>
      {products.length === 0 ? (
        <p>目前沒有產品</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th>名稱</th>
              <th>價格</th>
              <th>數量</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.price}</td>
                <td>{p.quantity}</td>
                <td>
                  <button onClick={() => handleDelete(idx)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Inventory;
