import React, { useEffect, useState } from "react";
import Modal from "react-modal";
import api from "../api"; // 你的 axios/fetch API

// 設定 app element 避免無障礙警告
Modal.setAppElement("#root");

export default function PeopleSelectModal({ ownerName, onSelect, onClose }) {
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    if (!ownerName) return;
    // 搜尋 API
    api.get(`/people/search/?name=${ownerName}`).then((res) => {
      setCandidates(res.data);
    });
  }, [ownerName]);

  return (
    <Modal
      isOpen={!!ownerName && candidates.length > 0}
      onRequestClose={onClose}
      contentLabel="選擇持有人"
      style={{
        overlay: { backgroundColor: "rgba(0,0,0,0.5)" },
        content: { maxWidth: "400px", margin: "auto", borderRadius: "8px" },
      }}
    >
      <h2>選擇持有人</h2>
      <button onClick={onClose} style={{ float: "right" }}>X</button>
      <ul>
        {candidates.map((p) => (
          <li
            key={p.id}
            onClick={() => {
              onSelect(p);
              onClose();
            }}
            style={{ cursor: "pointer", padding: "5px 0" }}
          >
            {p.name} - {p.id_number} - {p.department}
          </li>
        ))}
      </ul>
    </Modal>
  );
}
