import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import api from "../api";

Modal.setAppElement("#root"); // 設定React Modal根元素

export default function PeopleSelectModal({ isOpen, onClose, onSelect, searchName }) {
  const [peopleList, setPeopleList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!isOpen || !searchName) return;

    const fetchPeople = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await api.get("people/search/", { params: { name: searchName } });
        setPeopleList(res.data);
      } catch (err) {
        console.error(err);
        setError("搜尋失敗");
      } finally {
        setLoading(false);
      }
    };

    fetchPeople();
  }, [isOpen, searchName]);

  const handleSelect = (person) => {
    onSelect(person);
    onClose();
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      contentLabel="選擇持有人"
      style={{
        content: { maxWidth: "400px", margin: "auto", borderRadius: "8px", padding: "20px" },
        overlay: { backgroundColor: "rgba(0,0,0,0.5)" },
      }}
    >
      <h2>選擇持有人</h2>
      <button onClick={onClose} style={{ float: "right" }}>X</button>
      {loading && <p>搜尋中...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!loading && !error && (
        <ul>
          {peopleList.length === 0 && <li>沒有找到符合的員工</li>}
          {peopleList.map((p) => (
            <li key={p.id} style={{ marginBottom: "5px" }}>
              <button onClick={() => handleSelect(p)}>
                {p.name} ({p.id_number})
              </button>
            </li>
          ))}
        </ul>
      )}
    </Modal>
  );
}
