import React, { useState } from "react";
import PeopleSelectModal from "./PeopleSelectModal";
import api from "../api";

const AssetForm = () => {
  const [name, setName] = useState("");
  const [code, setCode] = useState("");
  const [type_, setType] = useState("");
  const [price, setPrice] = useState("");
  const [ownerName, setOwnerName] = useState("");
  const [ownerId, setOwnerId] = useState(null);

  const [modalOpen, setModalOpen] = useState(false);
  const [candidates, setCandidates] = useState([]);

  const handleOwnerBlur = async () => {
    if (!ownerName) return;

    try {
      const res = await api.get(`/people/?name=${ownerName}`);
      if (res.data.length === 1) {
        setOwnerId(res.data[0].id);
      } else if (res.data.length > 1) {
        setCandidates(res.data);
        setModalOpen(true);
      } else {
        alert("找不到員工");
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleSelectPerson = (person) => {
    setOwnerName(person.name);
    setOwnerId(person.id);
    setModalOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        name,
        asset_tag: code,
        type: type_,
        price,
        owner_user: ownerId,
      };
      await api.post("/inventory/assets/", payload);
      alert("新增成功");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input value={name} onChange={(e) => setName(e.target.value)} placeholder="名稱" />
        <input value={code} onChange={(e) => setCode(e.target.value)} placeholder="財產編號" />
        <input value={type_} onChange={(e) => setType(e.target.value)} placeholder="種類" />
        <input value={price} onChange={(e) => setPrice(e.target.value)} placeholder="價格" type="number" />
        <input
          value={ownerName}
          onChange={(e) => setOwnerName(e.target.value)}
          onBlur={handleOwnerBlur}
          placeholder="持有人"
        />
        <button type="submit">新增</button>
      </form>

      <PeopleSelectModal
        isOpen={modalOpen}
        candidates={candidates}
        onSelect={handleSelectPerson}
        onClose={() => setModalOpen(false)}
      />
    </>
  );
};

export default AssetForm;
