import React, { useEffect, useState } from "react";
import api from "../api";

const StockTransactionPage = () => {
  const [assets, setAssets] = useState([]);
  const [displayedAssets, setDisplayedAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [people, setPeople] = useState([]);
  const [filteredPeople, setFilteredPeople] = useState([]);

  const [transactionType, setTransactionType] = useState("OUT");
  const [selectedAssetTag, setSelectedAssetTag] = useState("");
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [selectedPersonId, setSelectedPersonId] = useState("");
  const [personSearch, setPersonSearch] = useState("");
  const [remark, setRemark] = useState("");

  // 取得資產列表
  useEffect(() => {
    const fetchAssets = async () => {
      try {
        const res = await api.get("/inventory/assets/");
        setAssets(res.data);
      } catch (err) {
        console.error(err);
        alert("載入資產失敗");
      }
    };
    fetchAssets();
  }, []);

  // 根據交易類型篩選資產
  useEffect(() => {
    const filtered = assets.filter((a) =>
      transactionType === "OUT" ? !a.owner_user : a.owner_user
    );
    setDisplayedAssets(filtered);
    setSelectedAssetTag(""); // 重置選擇
  }, [transactionType, assets]);

  // 取得部門列表
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const res = await api.get("/departments/");
        setDepartments(res.data);
      } catch (err) {
        console.error(err);
        alert("載入部門失敗");
      }
    };
    fetchDepartments();
  }, []);

  // 取得部門人員
  useEffect(() => {
    const fetchPeople = async () => {
      if (!selectedDepartment) {
        setPeople([]);
        setFilteredPeople([]);
        return;
      }
      try {
        const res = await api.get(`/people/?department=${selectedDepartment}`);
        setPeople(res.data);
        setFilteredPeople(res.data);
      } catch (err) {
        console.error(err);
        alert("載入員工失敗");
      }
    };
    fetchPeople();
  }, [selectedDepartment]);

  // 即時搜尋
  useEffect(() => {
    const filtered = people.filter((p) =>
      p.name.toLowerCase().includes(personSearch.toLowerCase()) ||
      p.email.toLowerCase().includes(personSearch.toLowerCase())
    );
    setFilteredPeople(filtered);
  }, [personSearch, people]);

  const handleSubmit = async () => {
    if (!selectedAssetTag) return alert("請選擇資產");
    if (transactionType === "OUT" && !selectedPersonId) return alert("請選擇員工");

    try {
      await api.post("/inventory/stock_transaction/", {
        asset_tag: selectedAssetTag,
        transaction_type: transactionType,
        person_id: Number(selectedPersonId),
        remark
      });
      alert("操作成功");

      setTransactionType("OUT");
      setSelectedAssetTag("");
      setSelectedDepartment("");
      setSelectedPersonId("");
      setPersonSearch("");
      setRemark("");

      // 重新載入資產
      const res = await api.get("/inventory/assets/");
      setAssets(res.data);
    } catch (err) {
      console.error(err.response || err);
      alert("操作失敗");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>商品出入庫</h1>

      <div>
        <label>交易類型：</label>
        <select
          value={transactionType}
          onChange={(e) => setTransactionType(e.target.value)}
        >
          <option value="OUT">出庫</option>
          <option value="IN">入庫</option>
        </select>
      </div>

      <div>
        <label>選擇資產：</label>
        <select
          value={selectedAssetTag}
          onChange={(e) => setSelectedAssetTag(e.target.value)}
        >
          <option value="">請選擇資產</option>
          {displayedAssets.map((a) => (
            <option key={a.asset_tag} value={a.asset_tag}>
              {a.product_name} ({a.product_code})
            </option>
          ))}
        </select>
      </div>

      {transactionType === "OUT" && (
        <>
          <div>
            <label>選擇部門：</label>
            <select
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
            >
              <option value="">-- 請選擇部門 --</option>
              {departments.map((d) => (
                <option key={d.id} value={d.name}>
                  {d.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>搜尋員工：</label>
            <input
              value={personSearch}
              onChange={(e) => setPersonSearch(e.target.value)}
              placeholder="姓名或Email"
            />
          </div>

          <div>
            <label>選擇員工：</label>
            <select
              value={selectedPersonId}
              onChange={(e) => setSelectedPersonId(e.target.value)}
            >
              <option value="">-- 請選擇員工 --</option>
              {filteredPeople.map((p) => (
                <option key={p.id} value={p.id}>
                  {p.name} ({p.email})
                </option>
              ))}
            </select>
          </div>
        </>
      )}

      <div>
        <label>備註：</label>
        <input
          value={remark}
          onChange={(e) => setRemark(e.target.value)}
        />
      </div>

      <button onClick={handleSubmit}>提交</button>

      <h2>資產列表</h2>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>#</th>
            <th>名稱</th>
            <th>財產編號</th>
            <th>種類</th>
            <th>價格</th>
            <th>持有人</th>
          </tr>
        </thead>
        <tbody>
          {assets.map((a, idx) => (
            <tr key={a.asset_tag}>
              <td>{idx + 1}</td>
              <td>{a.product_name}</td>
              <td>{a.product_code}</td>
              <td>{a.type}</td>
              <td>{a.price}</td>
              <td>{a.owner_user ? a.owner_user.name : "無"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StockTransactionPage;
