import React, { useEffect, useState } from "react";
import AssetForm from "../components/AssetForm";
import PeopleSelectModal from "../components//PeopleSelectModal";
import api from "../api";

const Inventory = () => {
  const [assets, setAssets] = useState([]);
  const [editingAsset, setEditingAsset] = useState(null);
  const [showPeopleModal, setShowPeopleModal] = useState(false);
  const [peopleCandidates, setPeopleCandidates] = useState([]);
  const [selectingForField, setSelectingForField] = useState(null);

  // 取得資產列表
  const fetchAssets = async () => {
    try {
      const res = await api.get("inventory/assets/");
      setAssets(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  const handleAddOrEdit = async (assetData) => {
    try {
      if (editingAsset) {
        const res = await api.put(`inventory/assets/${editingAsset.id}/`, assetData);
        setAssets((prev) =>
          prev.map((a) => (a.id === editingAsset.id ? res.data : a))
        );
      } else {
        const res = await api.post("inventory/assets/", assetData);
        setAssets((prev) => [...prev, res.data]);
      }
      setEditingAsset(null);
    } catch (err) {
      console.error(err.response?.data || err);
      alert("操作失敗，請查看 console");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("確定要刪除嗎？")) return;
    try {
      await api.delete(`inventory/assets/${id}/`);
      setAssets((prev) => prev.filter((a) => a.id !== id));
    } catch (err) {
      console.error(err);
      alert("刪除失敗");
    }
  };

  // 打開編輯
  const handleEdit = (asset) => setEditingAsset(asset);
  const cancelEdit = () => setEditingAsset(null);

  // People Modal
  const handleOpenPeopleModal = async (fieldName, nameQuery) => {
    setSelectingForField(fieldName);
    try {
      const res = await api.get(`/people/search/?name=${nameQuery}`);
      if (res.data.length === 1) {
        // 直接選擇
        handleSelectPerson(res.data[0]);
      } else if (res.data.length > 1) {
        setPeopleCandidates(res.data);
        setShowPeopleModal(true);
      } else {
        alert("找不到該員工");
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleSelectPerson = (person) => {
    if (editingAsset && selectingForField) {
      setEditingAsset((prev) => ({
        ...prev,
        [selectingForField]: person,
      }));
    }
    setShowPeopleModal(false);
    setSelectingForField(null);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>庫存管理</h1>

      <AssetForm
        asset={editingAsset}
        onCancel={cancelEdit}
        onSave={handleAddOrEdit}
        onOpenPeopleModal={handleOpenPeopleModal}
      />

      <h2>資產列表</h2>
      {assets.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th>名稱</th>
              <th>財產編號</th>
              <th>種類</th>
              <th>價格</th>
              <th>持有人</th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {assets.map((a, idx) => (
              <tr key={a.id}>
                <td>{idx + 1}</td>
                <td>{a.product.name}</td>
                <td>{a.asset_tag}</td>
                <td>{a.product.type}</td>
                <td>{a.product.price}</td>
                <td>{a.owner_user?.name ?? "無"}</td>
                <td>
                  <button onClick={() => handleEdit(a)} style={{ marginRight: 5 }}>
                    編輯
                  </button>
                  <button onClick={() => handleDelete(a.id)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {showPeopleModal && (
        <PeopleSelectModal
          candidates={peopleCandidates}
          onClose={() => setShowPeopleModal(false)}
          onSelect={handleSelectPerson}
        />
      )}
    </div>
  );
};

export default Inventory;
