import React, { useState, useEffect } from "react";
import GenericForm from "../components/GenericForm";
import api from "../api";

const PeopleManagement = () => {
  const [people, setPeople] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingPerson, setEditingPerson] = useState(null); // 編輯用
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });

  const fields = [
    { name: "name", label: "姓名", type: "text" },
    { name: "id_number", label: "身分證字號", type: "text" },
    { name: "email", label: "Email", type: "email" },
    { name: "phone", label: "電話", type: "text" },
    { name: "department", label: "部門", type: "text" },
  ];

  // 初始抓資料
  useEffect(() => {
    const fetchPeople = async () => {
      try {
        const res = await api.get("people");
        setPeople(res.data);
      } catch (err) {
        console.error("抓取人員資料失敗", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPeople();
  }, []);

  // 單筆新增 / 編輯
  const handleAddSingle = async (person) => {
    try {
      if (editingPerson) {
        const res = await api.put(`people/${editingPerson.id}`, person);
        setPeople(people.map(p => (p.id === editingPerson.id ? res.data : p)));
        setEditingPerson(null);
      } else {
        const res = await api.post("people", person);
        setPeople([...people, res.data]);
      }
    } catch (err) {
      console.error("新增/編輯失敗", err);
      alert(err.response?.data?.error || "操作失敗");
    }
  };

  // CSV 匯入
  const handleAddCSV = async (csvData) => {
    try {
      const added = [];
      for (const r of csvData) {
        const res = await api.post("people", {
          name: r.name,
          id_number: r.id_number,
          email: r.email,
          phone: r.phone,
          department: r.department,
        });
        added.push(res.data);
      }
      setPeople([...people, ...added]);
    } catch (err) {
      console.error("CSV 匯入失敗", err);
      alert("CSV 匯入失敗");
    }
  };

  // 刪除
  const handleDelete = async (id) => {
    if (!window.confirm("確定刪除嗎？")) return;
    try {
      await api.delete(`people/${id}`);
      setPeople(people.filter(p => p.id !== id));
    } catch (err) {
      console.error("刪除失敗", err);
      alert("刪除失敗");
    }
  };

  // 編輯
  const handleEdit = (person) => {
    setEditingPerson(person);e
  };

  // 排序功能
  const handleSort = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      direction = "desc";
    }
    setSortConfig({ key, direction });
  };

  const sortedPeople = [...people].sort((a, b) => {
    if (!sortConfig.key) return 0;
    const valA = a[sortConfig.key] ? a[sortConfig.key].toString().toLowerCase() : "";
    const valB = b[sortConfig.key] ? b[sortConfig.key].toString().toLowerCase() : "";
    if (valA < valB) return sortConfig.direction === "asc" ? -1 : 1;
    if (valA > valB) return sortConfig.direction === "asc" ? 1 : -1;
    return 0;
  });

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>人員管理</h1>

      <GenericForm
        fields={fields}
        onAddSingle={handleAddSingle}
        onAddCSV={handleAddCSV}
        initialValues={editingPerson || {}}
      />

      <h2>人員列表</h2>
      {people.length === 0 ? (
        <p>目前沒有資料</p>
      ) : (
        <table border="1" cellPadding="5" cellSpacing="0">
          <thead>
            <tr>
              <th>#</th>
              <th onClick={() => handleSort("name")} style={{ cursor: "pointer" }}>
                姓名 {sortConfig.key === "name" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
              </th>
              <th onClick={() => handleSort("id_number")} style={{ cursor: "pointer" }}>
                身分證字號 {sortConfig.key === "id_number" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
              </th>
              <th onClick={() => handleSort("email")} style={{ cursor: "pointer" }}>
                Email {sortConfig.key === "email" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
              </th>
              <th onClick={() => handleSort("phone")} style={{ cursor: "pointer" }}>
                電話 {sortConfig.key === "phone" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
              </th>
              <th onClick={() => handleSort("department")} style={{ cursor: "pointer" }}>
                部門 {sortConfig.key === "department" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
              </th>
              <th>操作</th>
            </tr>
          </thead>
          <tbody>
            {sortedPeople.map((p, idx) => (
              <tr key={p.id ?? idx}>
                <td>{idx + 1}</td>
                <td>{p.name}</td>
                <td>{p.id_number}</td>
                <td>{p.email}</td>
                <td>{p.phone}</td>
                <td>{p.department}</td>
                <td>
                  <button onClick={() => handleEdit(p)}>編輯</button>{" "}
                  <button onClick={() => handleDelete(p.id)}>刪除</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PeopleManagement;
